// Get OCR result
const ocrResult = $json;

// Extract the text
let extractedText = '';

if (ocrResult.ParsedResults && ocrResult.ParsedResults[0]) {
  extractedText = ocrResult.ParsedResults[0].ParsedText;
} else {
  return {
    json: {
      error: 'OCR failed to extract text',
      qualified: false,
      redFlags: ['Could not read invoice text']
    }
  };
}

// Helper function to parse dates correctly without timezone issues
function parseDate(dateString) {
  // Try to parse dates like "October 1, 2025"
  const date = new Date(dateString + ' 12:00:00 UTC'); // Add noon UTC to avoid timezone shifts

  if (!isNaN(date)) {
    const year = date.getUTCFullYear();
    const month = String(date.getUTCMonth() + 1).padStart(2, '0');
    const day = String(date.getUTCDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  }

  return '';
}

// Parse invoice fields using regex
const invoiceData = {
  qualified: true,
  vendor: '',
  invoiceNumber: '',
  amount: 0,
  currency: 'USD',
  invoiceDate: '',
  dueDate: '',
  description: '',
  confidence: 100,
  redFlags: []
};

// Extract Vendor (line after "From:")
const vendorMatch = extractedText.match(/From:\s*([^\n]+)/i);
if (vendorMatch) {
  invoiceData.vendor = vendorMatch[1].trim();
}

// Extract Invoice Number
const invoiceNumMatch = extractedText.match(/Invoice Number:\s*([^\n]+)/i);
if (invoiceNumMatch) {
  invoiceData.invoiceNumber = invoiceNumMatch[1].trim();
}

// Extract Amount (from "Total:" line) - CRITICAL!
const totalMatch = extractedText.match(/Total:\s*\$\s*([0-9,]+\.?[0-9]*)/i);
if (totalMatch) {
  invoiceData.amount = parseFloat(totalMatch[1].replace(/,/g, ''));
}

// If Total not found, try Amount line
if (invoiceData.amount === 0) {
  const amountMatch = extractedText.match(/Amount:\s*\$\s*([0-9,]+\.?[0-9]*)/i);
  if (amountMatch) {
    invoiceData.amount = parseFloat(amountMatch[1].replace(/,/g, ''));
  }
}

// Extract Currency
const currencyMatch = extractedText.match(/(USD|EUR|GBP|CAD)/i);
if (currencyMatch) {
  invoiceData.currency = currencyMatch[1].toUpperCase();
}

// Extract Invoice Date - FIXED!
const dateMatch = extractedText.match(/Date:\s*([^\n]+)/i);
if (dateMatch) {
  const dateStr = dateMatch[1].trim();
  invoiceData.invoiceDate = parseDate(dateStr);
}

// Extract Due Date - FIXED!
const dueMatch = extractedText.match(/Due Date:\s*([^\n]+)/i);
if (dueMatch) {
  const dateStr = dueMatch[1].trim();
  invoiceData.dueDate = parseDate(dateStr);
}

// Extract Description
const descMatch = extractedText.match(/Description:\s*([^\n]+)/i);
if (descMatch) {
  invoiceData.description = descMatch[1].trim();
}

// Validation checks
if (!invoiceData.vendor) {
  invoiceData.qualified = false;
  invoiceData.redFlags.push('Missing vendor name');
}

if (!invoiceData.invoiceNumber) {
  invoiceData.qualified = false;
  invoiceData.redFlags.push('Missing invoice number');
}

if (invoiceData.amount === 0) {
  invoiceData.qualified = false;
  invoiceData.redFlags.push('Missing or invalid amount');
}

if (invoiceData.amount > 10000) {
  invoiceData.qualified = false;
  invoiceData.redFlags.push('Amount exceeds $10,000');
}

return {
  json: invoiceData
};

const sheetData = $node["Append or update row in sheet"].json;

return {
  json: {
    vendor: sheetData.Vendor,
    amount: sheetData.Amount,
    currency: sheetData.Currency,
    invoiceNumber: sheetData["Invoice Number"],
    invoiceDate: sheetData["Invoice Date"],  // ‚Üê ADD THIS LINE!
    dueDate: sheetData["Due Date"],
    description: sheetData.Description
  }
};

const invoiceData = { ...$node["Format for Slack"].json };

// Strip off any vendor or itemCode field that might be passed
delete invoiceData.vendor;
delete invoiceData.itemCode;
delete invoiceData["Item Code Name or ID"]; // Just in case

return [{ json: invoiceData }];

