const actionType = $('Webhook').first().json.body.action.type;
const username = $('username').first().json.username;
const addedBy = $('Webhook').first().json.body.action.memberCreator.fullName;
const cardName = $('Webhook').first().json.body.action.data.card.name;
const date = new Date($('Webhook').first().json.body.action.date).toLocaleDateString('en-US');
const description = $('Webhook').first().json.body.model.desc
const url = $('Webhook').first().json.body.model.url
const dueDate = new Date($('Webhook').first().json.body.model.due).toLocaleDateString('en-US')

function escapeMarkdownV2(text) {
  return text.replace(/[_*[\]()~`>#+\-=|{}.!]/g, '\\$&');
}

const escapedCardName = escapeMarkdownV2(cardName);
const escapedUsername = escapeMarkdownV2(username);

const addMessages = {
  "1": `üìù Hey ${escapedUsername}, welcome aboard to a new card *${escapedCardName}*! Don‚Äôt mess it up‚Ä¶`,
  "2": `üìù Yo ${escapedUsername}! You‚Äôve been tossed onto the *${escapedCardName}* card. Good luck, champ!`,
  "3": `üìù ${escapedUsername}, congrats! You‚Äôre now officially stuck with *${escapedCardName}*. Strap on a pair and get it done!`,
  "4": `üìù Attention ${escapedUsername}! You‚Äôve been drafted into *${escapedCardName}*. Try not to cry.`,
  "5": `üìù ${escapedUsername}, get ready to shine on *${escapedCardName}*. Or fail spectacularly.`
};

const removeMessages = {
  "1": `üî• Oh ${escapedUsername}, freedom at last! You‚Äôre off *${escapedCardName}*. Was the assigner even paying attention?`,
  "2": `üî• ${escapedUsername}, you‚Äôre free from *${escapedCardName}*. Don‚Äôt get too excited, there‚Äôs always another card!`,
  "3": `üî• ${escapedUsername}, you‚Äôve been relieved from *${escapedCardName}* card. You lucky soul!`,
  "4": `üî• ${escapedUsername} vanished from *${escapedCardName}*. Did you even try? You have let your team down.`,
  "5": `üî• ${escapedUsername}, no more *${escapedCardName}* for you. Time to relax‚Ä¶ or not.`
};

let message = "";

if (actionType === "addMemberToCard") {
  const keys = Object.keys(addMessages);
  const randomKey = keys[Math.floor(Math.random() * keys.length)];
  message = addMessages[randomKey];
} 
else if (actionType === "removeMemberFromCard") {
  const keys = Object.keys(removeMessages);
  const randomKey = keys[Math.floor(Math.random() * keys.length)];
  message = removeMessages[randomKey];
}
else {
  message = `‚ÑπÔ∏è Trello: ${actionType} on "*${cardName}*" by ${username} (triggered by ${addedBy}) at ${date}`;
}

return { 
  finalMessage: message,
  actionType: actionType,
  username: username,
  addedBy: addedBy,
  cardName: cardName,
  date: date,
  description,
  url, 
  dueDate
};
