function getRandomItem(arr) {
  // Calculate a random index and return the item at that index
  return arr[Math.floor(Math.random() * arr.length)];
}

const myChoices = ["131", "130", "129", "70"];

// Call the function and store the returned value in a variable
const selected = getRandomItem(myChoices);

// Return as an array of items, each with a { json } property
return [
  { json: { choice: selected } }
];
