// This code fixes malformed JSON strings and merges the contents into a single object.

// 1. Get the raw, potentially broken string from the previous node.
const rawJsonString = $input.first().json.output.string;

// 2. **CRITICAL FIX STEP**: Add missing quotes around keys.
// This regex finds keys like {key: or ,key: and changes them to {"key":
// This repairs the common error made by the agent.
const fixedJsonString = rawJsonString.replace(/([{,])\s*([a-zA-Z0-9_]+)\s*:/g, '$1"$2":');

const mergedObject = {};

try {
  // 3. Parse the *fixed* string into a real JavaScript array.
  const dataArray = JSON.parse(fixedJsonString);

  // 4. Loop through the array and merge all objects into one.
  for (const item of dataArray) {
    Object.assign(mergedObject, item);
  }

  // 5. **Handle Nested Strings**: After merging, check all values.
  // If any value is a string that looks like JSON, parse it too.
  for (const key in mergedObject) {
    const value = mergedObject[key];
    if (typeof value === 'string') {
      const trimmedValue = value.trim();
      if ((trimmedValue.startsWith('[') && trimmedValue.endsWith(']')) || (trimmedValue.startsWith('{') && trimmedValue.endsWith('}'))) {
        try {
          // If it looks like JSON, parse it and replace the string.
          mergedObject[key] = JSON.parse(trimmedValue);
        } catch (e) {
          // If parsing fails, it's not valid JSON. Leave it as a string.
        }
      }
    }
  }

} catch (error) {
  // If the string is still not valid JSON after the fix, this will prevent a crash.
  throw new Error('The input text could not be parsed as JSON, even after attempting a fix. Error: ' + error.message);
}

// 6. Return the final, single, merged item.
return [{
  json: mergedObject
}];
