const input = $('Telegram Trigger').first().json.message.text
let command, rest;

if (input.startsWith('/')) {
  const splitIndex = input.indexOf(' ');
  if (splitIndex === -1) {
    // No space found, entire input is the command
    command = input;
    rest = "";
  } else {
    command = input.substring(0, splitIndex);
    rest = input.substring(splitIndex + 1);
  }
} else {
  command = "";
  rest = input;
}

return [{
  json: {
    command: command,
    rest: rest
  }
}];

