const data = items[0].json.output;

let cleaned = data.trim();

if (cleaned.startsWith("```")) {
  cleaned = cleaned
    .replace(/^```(json)?/i, "")
    .replace(/```$/, "")
    .trim();
}

let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (e) {
  throw new Error("‚ùå Cannot parse JSON from AI Agent: " + e.message + "\nData: " + cleaned);
}

return [
  {
    json: parsed
  }
];

const rows = items.map(i => i.json);

// keep last 48h only
const cutoff = Date.now() - 48 * 60 * 60 * 1000;
const recent = rows.filter(r => new Date(r.ts).getTime() >= cutoff);

// group by coin
const byCoin = {};
for (const r of recent) {
  const c = String(r.coin).toLowerCase();
  if (!byCoin[c]) byCoin[c] = [];
  byCoin[c].push(r);
}

// downsample: 30m buckets, max 120 points
function downsample30m(sortedArr) {
  const out = [];
  let lastBucket = -1;
  for (const r of sortedArr) {
    const t = new Date(r.ts).getTime();
    const bucket = Math.floor(t / (30 * 60 * 1000));
    if (bucket !== lastBucket) {
      out.push([new Date(t).toISOString(), Number(r.price_usd ?? r.price)]);
      lastBucket = bucket;
    }
  }
  return out.slice(-120);
}

const out = [];
for (const [coin, arr] of Object.entries(byCoin)) {
  arr.sort((a,b)=> new Date(a.ts) - new Date(b.ts));
  if (!arr.length) continue;
  const last = arr[arr.length - 1];

  out.push({
    json: {
      coin,
      latest: {
        ts: last.ts,
        price: Number(last.price_usd ?? last.price),
        change_1h: typeof last.change_1h === 'number' ? last.change_1h : null,
        change_24h: typeof last.change_24h === 'number' ? last.change_24h : null
      },
      series: downsample30m(arr)
    }
  });
}

return out;

const input = items[0].json;
const out = [];

for (const [key, val] of Object.entries(input)) {
  if (key.toLowerCase().includes('coin') && val) {
    out.push({ json: { coin: val } });
  }
}

return out;

