// Build combined HTML email (HTML only, GMT-flex)
// Reads GMT offset from node "Recipient Email" -> field `timezone`
// Accepted: "+8", "8", "GMT+7", "UTC+5:30", "-3", "-03:30", 8

function safeParse(x) {
  if (!x && x !== 0) return {};
  if (typeof x === 'object') return x;
  try { return JSON.parse(x); } catch { return {}; }
}
function fmtUSD(n) {
  if (n == null || isNaN(n)) return '-';
  return new Intl.NumberFormat('en-US', { maximumFractionDigits: 2 }).format(Number(n));
}
function dirWord(d) {
  const x = String(d || '').toLowerCase();
  if (x === 'up') return 'likely to rise';
  if (x === 'down') return 'likely to fall';
  return 'flat';
}
function confWord(c) {
  const x = String(c || '').toLowerCase();
  if (x === 'high') return 'high';
  if (x === 'medium') return 'medium';
  return 'low';
}

// ---- GMT offset helpers ----
function parseGmtOffset(raw) {
  if (raw === undefined || raw === null || raw === '') return { minutes: 480, label: "GMT+08:00" }; // default +8
  let s = String(raw).trim().toUpperCase();      // "GMT+7", "UTC+5:30", "+8", "8", "-03:30"
  s = s.replace(/^GMT|^UTC/, '');
  if (s === '') s = '+0';
  if (!/^[-+]/.test(s)) s = (Number(s) >= 0 ? '+' : '') + s; // allow "8" -> "+8"

  const m = s.match(/^([+-])(\d{1,2})(?::?(\d{2}))?$/);
  if (!m) return { minutes: 480, label: "GMT+08:00" };
  const sign = m[1] === '-' ? -1 : 1;
  const hh = Math.min(14, Math.max(0, parseInt(m[2], 10)));
  const mm = m[3] ? Math.min(59, Math.max(0, parseInt(m[3], 10))) : 0;
  const minutes = sign * (hh * 60 + mm);
  const lab = `GMT${sign === 1 ? '+' : '-'}${String(hh).padStart(2,'0')}:${String(mm).padStart(2,'0')}`;
  return { minutes, label: lab };
}

function toLocalHHMM(iso, offsetMin, label) {
  if (!iso) return null;
  try {
    const d = new Date(iso);
    const local = new Date(d.getTime() + offsetMin * 60000);
    const t = local.toISOString().substring(11,16); // HH:MM
    return `${t} ${label}`;
  } catch { return null; }
}
function fmtWindowLocal(w, offsetMin, label) {
  if (!w || !w.start_iso || !w.end_iso) return null;
  const s = toLocalHHMM(w.start_iso, offsetMin, label);
  const e = toLocalHHMM(w.end_iso, offsetMin, label);
  return (s && e) ? `${s} ‚Äì ${e}` : null;
}

// ---- read recipient tz ----
let tzRaw = null;
try { tzRaw = $('Recipient Email + Timezone').first().json.timezone ?? null; } catch {}
const tz = parseGmtOffset(tzRaw);
const tzLabel = tz.label;
const tzMinutes = tz.minutes;

// ---- build per-coin HTML cards ----
const cards = items.map(it => {
  const raw = it.json && ('output' in it.json) ? it.json.output : it.json;
  const o = safeParse(raw);

  const coin = (o.coin || '').toUpperCase() || 'UNKNOWN';
  const price = o.latest?.price ?? o.price ?? null;
  const ch1 = (o.latest?.change_1h ?? o.change_1h);
  const ch24 = (o.latest?.change_24h ?? o.change_24h);
  const d6 = o.direction_6h || 'flat';
  const d12 = o.direction_12h || 'flat';
  const d24 = o.direction_24h || 'flat';
  const upWin = fmtWindowLocal(o.best_up_window, tzMinutes, tzLabel);
  const downWin = fmtWindowLocal(o.best_down_window, tzMinutes, tzLabel);
  const conf = confWord(o.confidence);

  const html = [
    `<div style="padding:12px;border-radius:8px;border:1px solid #e6e6e6;margin-bottom:10px;background:#ffffff;">`,
    `  <div style="display:flex;justify-content:space-between;align-items:center">`,
    `    <div style="font-weight:700;font-size:16px;">${coin}</div>`,
    `    <div style="font-size:14px;color:#333">Price: $${fmtUSD(price)}</div>`,
    `  </div>`,
    `  <div style="margin-top:8px;font-size:13px;color:#333">`,
    `    <div><strong>Forecast</strong>: 6h ‚Äî ${dirWord(d6)}, 12h ‚Äî ${dirWord(d12)}, 24h ‚Äî ${dirWord(d24)}</div>`,
    (typeof ch1 !== 'undefined' && ch1 !== null) ? `    <div>1h change: ${Number(ch1)}%</div>` : ``,
    (typeof ch24 !== 'undefined' && ch24 !== null) ? `    <div>24h change: ${Number(ch24)}%</div>` : ``,
    upWin   ? `    <div style="margin-top:6px;color:green;"><strong>Favorable for upside:</strong> ${upWin}</div>` : ``,
    downWin ? `    <div style="margin-top:6px;color:red;"><strong>Prone to downside:</strong> ${downWin}</div>` : ``,
    `    <div style="margin-top:8px;font-size:12px;color:#666;"><em>Model confidence: ${conf}</em></div>`,
    `  </div>`,
    `</div>`
  ].filter(Boolean).join('\n');

  return html;
});

// fallback if empty
if (cards.length === 0) {
  cards.push(`<div style="padding:12px;border-radius:8px;border:1px solid #e6e6e6;background:#fff;">No forecast available.</div>`);
}

// header time in recipient's GMT
const now = new Date();
const nowLocal = new Date(now.getTime() + tzMinutes * 60000);
const nowLabel = `${nowLocal.toISOString().substring(0,10)} ${nowLocal.toISOString().substring(11,19)} ${tzLabel}`;

// final HTML
const body_html = `
<div style="font-family:Arial,Helvetica,sans-serif;background:#f7f7f7;padding:16px;">
  <div style="max-width:700px;margin:0 auto;">
    <div style="color:#111;line-height:1.4;">
      <h2 style="margin:0 0 8px 0;font-size:18px;">Crypto Forecast ‚Äî ${nowLabel}</h2>
      <div style="margin-bottom:10px;color:#666;font-size:12px;">Note: this is not financial advice. For educational purposes only.</div>
    </div>
    <div>
      ${cards.join('<div style="height:8px"></div>')}
    </div>
  </div>
</div>`.trim();

return [{ json: { body_html } }];
