File: src/lib/types/workflow.ts
Lines 1-232:
  1 | /**
  2 |  * N8N Workflows 核心类型定义
  3 |  * 定义工作流、分类、作者等数据结构
  4 |  */
  5 | 
  6 | /**
  7 |  * 工作流复杂度级别
  8 |  */
  9 | export type ComplexityLevel = 'beginner' | 'intermediate' | 'advanced';
 10 | 
 11 | /**
 12 |  * 作者信息
 13 |  */
 14 | export interface Author {
 15 |   id: number;
 16 |   slug: string;
 17 |   name: string;
 18 |   avatar?: string;
 19 |   bio?: string;
 20 |   website?: string;
 21 |   twitter?: string;
 22 |   github?: string;
 23 |   workflowCount?: number;
 24 | }
 25 | 
 26 | /**
 27 |  * 分类信息
 28 |  */
 29 | export interface Category {
 30 |   id: number;
 31 |   slug: string;
 32 |   name: string;
 33 |   description?: string;
 34 |   icon?: string;
 35 |   color?: string;
 36 |   workflowCount?: number;
 37 | }
 38 | 
 39 | /**
 40 |  * 工作流数据结构
 41 |  */
 42 | export interface Workflow {
 43 |   // 基础信息
 44 |   id: number;
 45 |   slug: string;
 46 |   title: string;
 47 |   description: string;
 48 | 
 49 |   // 媒体资源
 50 |   thumbnail: string;
 51 |   screenshots?: string[];
 52 | 
 53 |   // 作者信息
 54 |   author: Author;
 55 | 
 56 |   // 分类和标签
 57 |   categories: string[];
 58 |   tags?: string[];
 59 | 
 60 |   // 复杂度和定价
 61 |   complexityLevel: ComplexityLevel;
 62 |   price: number; // 0 表示免费
 63 | 
 64 |   // 统计数据
 65 |   visitors: number;
 66 |   downloads: number;
 67 |   likes?: number;
 68 | 
 69 |   // 时间戳
 70 |   createdAt: string;
 71 |   updatedAt: string;
 72 |   publishedAt: string;
 73 |   date?: string; // 格式化后的日期字符串
 74 | 
 75 |   // SEO
 76 |   metaTitle?: string;
 77 |   metaDescription?: string;
 78 | 
 79 |   // 工作流详情
 80 |   nodes?: number; // 节点数量
 81 |   version?: string; // n8n 版本
 82 |   sourceUrl?: string; // 原始链接
 83 | }
 84 | 
 85 | /**
 86 |  * 逻辑块定义 - 用于详情页展示
 87 |  */
 88 | export interface LogicalBlock {
 89 |   id: string;
 90 |   order: number;
 91 |   title: string;
 92 |   description: string;
 93 |   blockType?: 'trigger' | 'action' | 'condition';
 94 | }
 95 | 
 96 | /**
 97 |  * 节点详细信息 - 用于 Block-by-Block Analysis
 98 |  */
 99 | export interface NodeDetail {
100 |   name: string;
101 |   type: string;
102 |   role: string;
103 |   config?: Record<string, any>;
104 |   connections?: string[];
105 |   edgeCases?: string[];
106 |   configDescription?: string;
107 |   failureHandling?: string;
108 | }
109 | 
110 | /**
111 |  * 工作流概览 - 用于详情页概览部分
112 |  */
113 | export interface WorkflowOverview {
114 |   title: string;
115 |   workflowName: string;
116 |   description: string;
117 |   features?: string[];
118 |   useCases?: string[];
119 | }
120 | 
121 | /**
122 |  * 扩展的工作流详细信息 (用于详情页)
123 |  */
124 | export interface WorkflowDetailed extends Workflow {
125 |   // 文档内容
126 |   disclaimer?: string; // 免责声明
127 |   overview?: WorkflowOverview;
128 |   logicalBlocks?: LogicalBlock[];
129 |   nodeDetails?: NodeDetail[];
130 | 
131 |   // 内容管理
132 |   updatedContentAt?: string; // 内容最后更新时间
133 | 
134 |   // 扩展字段
135 |   relatedWorkflows?: number[]; // 相关工作流ID数组
136 | }
137 | 
138 | /**
139 |  * API 响应 - 加载更多工作流
140 |  */
141 | export interface LoadMoreResponse {
142 |   workflows: Workflow[];
143 |   total: number;
144 |   offset: number;
145 |   limit: number;
146 |   hasMore: boolean;
147 | }
148 | 
149 | /**
150 |  * API 查询参数 - 加载更多工作流
151 |  */
152 | export interface LoadMoreParams {
153 |   type?: 'all' | 'popular' | 'recent';
154 |   category?: string;
155 |   author?: string;
156 |   complexity?: ComplexityLevel;
157 |   sort?: 'date-desc' | 'date-asc' | 'visitors-desc' | 'downloads-desc';
158 |   price?: 'all' | 'free' | 'paid';
159 |   time?: 'all' | 'today' | 'week' | 'month';
160 |   offset: number;
161 |   limit: number;
162 | }
163 | 
164 | /**
165 |  * 搜索查询参数
166 |  */
167 | export interface SearchParams {
168 |   q: string; // 搜索关键词
169 |   category?: string;
170 |   complexity?: ComplexityLevel;
171 |   price?: 'all' | 'free' | 'paid';
172 |   offset?: number;
173 |   limit?: number;
174 | }
175 | 
176 | /**
177 |  * 搜索响应
178 |  */
179 | export interface SearchResponse {
180 |   results: Workflow[];
181 |   total: number;
182 |   query: string;
183 | }
184 | 
185 | /**
186 |  * 统计数据
187 |  */
188 | export interface Stats {
189 |   totalWorkflows: number;
190 |   totalAuthors: number;
191 |   totalCategories: number;
192 |   totalDownloads: number;
193 |   recentWorkflows: number; // 最近 30 天
194 | }
195 | 
196 | /**
197 |  * SEO Meta 数据
198 |  */
199 | export interface SEOMeta {
200 |   title: string;
201 |   description: string;
202 |   canonical?: string;
203 |   image?: string;
204 |   type?: 'website' | 'article';
205 |   publishedTime?: string;
206 |   modifiedTime?: string;
207 |   author?: string;
208 |   tags?: string[];
209 |   noindex?: boolean;
210 | }
211 | 
212 | /**
213 |  * 导航菜单项
214 |  */
215 | export interface NavItem {
216 |   label: string;
217 |   href: string;
218 |   icon?: string;
219 |   external?: boolean;
220 | }
221 | 
222 | /**
223 |  * 分页信息
224 |  */
225 | export interface Pagination {
226 |   currentPage: number;
227 |   totalPages: number;
228 |   pageSize: number;
229 |   totalItems: number;
230 |   hasNext: boolean;
231 |   hasPrev: boolean;
232 | }
