/**
 * 工作流详情页（动态路由）
 * 展示单个工作流的完整信息
 */

import MainLayout from '../../layouts/MainLayout.astro';
import WorkflowDetail from '../../components/workflow/WorkflowDetail.astro';
import WorkflowDetailedView from '../../components/workflow/WorkflowDetailedView.astro';
import WorkflowMeta from '../../components/workflow/WorkflowMeta.astro';
import RelatedWorkflows from '../../components/workflow/RelatedWorkflows.astro';
import Breadcrumb from '../../components/ui/Breadcrumb.astro';
import { getWorkflows, getWorkflowBySlug, getWorkflowDetailedById } from '../../lib/services/workflows';
import { generateWorkflowSchema, generateBreadcrumbSchema, combineSchemas } from '../../lib/utils/schema';
import type { Workflow, WorkflowDetailed } from '../../lib/types/workflow';

// 生成静态路径
export async function getStaticPaths() {
  try {
    const result = await getWorkflows({ limit: 1000, offset: 0 });
    return result.workflows.map((workflow) => ({
      params: { slug: workflow.slug },
      props: { workflow },
    }));
  } catch (error) {
    console.error('Error generating workflow paths:', error);
    return [];
  }
}

const { slug } = Astro.params;
let { workflow } = Astro.props;

// 获取详细工作流信息（含结构化内容）
let workflowDetailed: WorkflowDetailed = workflow as WorkflowDetailed;
try {
  if (workflow.id) {
    const detailed = await getWorkflowDetailedById(workflow.id);
    if (detailed) {
      workflowDetailed = detailed;
    }
  }
} catch (error) {
  console.error('Error fetching detailed workflow:', error);
  // 降级到基础工作流信息
}

// 获取相关工作流（同分类或同作者）
let relatedWorkflows: Workflow[] = [];
try {
  if (workflow.categories && workflow.categories.length > 0) {
    const result = await getWorkflows({
      category: workflow.categories[0].toLowerCase().replace(/\s+/g, '-'),
      limit: 4,
      offset: 0,
    });
    // 过滤掉当前工作流
    relatedWorkflows = result.workflows.filter((w) => w.id !== workflow.id);
  }
} catch (error) {
  console.error('Error fetching related workflows:', error);
}

// 面包屑导航
const breadcrumbItems = [
  { name: 'Workflows', url: '/' },
  { name: workflow.title, url: `/workflows/${slug}` }
];

const siteUrl = import.meta.env.PUBLIC_SITE_URL || 'https://n8nworkflow.com';

// 生成结构化数据
const workflowSchema = generateWorkflowSchema(workflow, siteUrl);
const breadcrumbSchema = generateBreadcrumbSchema(breadcrumbItems, siteUrl);
const combinedSchema = combineSchemas(workflowSchema, breadcrumbSchema);

// 读取工作流 JSON 数据
let workflowJson = null;
try {
  const workflowJsonPath = `/data/workflows/${slug}/${workflow.id}.json`;
  const response = await fetch(new URL(workflowJsonPath, Astro.url.origin));
  if (response.ok) {
    workflowJson = await response.json();
  }
} catch (error) {
  console.error('Error loading workflow JSON:', error);
}

// SEO 配置
const seoConfig = {
  title: `${workflow.title} - N8N Workflows`,
  description: workflow.description || `Download and use this ${workflow.complexityLevel} n8n workflow created by ${workflow.author.name}.`,
  canonical: `/workflows/${slug}`,
  image: workflow.featuredImage || workflow.thumbnail,
  type: 'article' as const,
  publishedTime: workflow.publishedAt,
  modifiedTime: workflow.updatedAt,
  author: workflow.author.name,
  tags: workflow.categories,
  schema: combinedSchema,
};

---

<MainLayout {...seoConfig}>
  <div class="w-full min-h-screen">
    <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Breadcrumb */}
      <Breadcrumb items={breadcrumbItems.map((item) => ({ label: item.name, href: item.url }))} />

      {/* Title */}
      <h1 class="text-3xl md:text-4xl font-bold text-gray-900 dark:text-white mt-4 mb-8">
        {workflow.title}
      </h1>

      {/* Workflow Preview */}
      <div class="w-full aspect-video bg-white dark:bg-gray-800 rounded-xl border border-gray-200 dark:border-gray-700 overflow-hidden shadow-sm mb-8" id="workflow-viewer">
        {workflowJson ? (
          <div class="w-full h-full" data-workflow-json={JSON.stringify(workflowJson)}></div>
        ) : (
          <img
            src={workflow.featuredImage || '/placeholder-workflow.webp'}
            alt={workflow.title}
            class="w-full h-full object-cover"
          />
        )}
      </div>

      {/* Mobile Action Buttons */}
      <div class="lg:hidden grid grid-cols-1 sm:grid-cols-2 gap-4 mb-8">
        <a href={workflow.sourceUrl || '#'} class="w-full py-3 px-4 bg-gray-900 dark:bg-white text-white dark:text-gray-900 rounded-xl font-bold text-center flex items-center justify-center gap-2 hover:bg-gray-800 dark:hover:bg-gray-100 transition-colors">
          <span>View on n8n.io</span>
          <svg class="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" /></svg>
        </a>
        <button class="w-full py-3 px-4 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 text-gray-900 dark:text-white rounded-xl font-bold flex items-center justify-center gap-2 hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors">
          <svg class="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4" /></svg>
          <span>Download Workflow</span>
        </button>
      </div>

      {/* Promo/Ads Row */}
      <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-12">
        // ... existing code ...
      </div>

      {/* Main Content - 2 Column Layout */}
      <div class="mt-6">
         {/* 使用新的 WorkflowDetailedView 如果有详情内容 */}
         {workflowDetailed.overview ||
          workflowDetailed.logicalBlocks ||
          workflowDetailed.nodeDetails ? (
           <WorkflowDetailedView workflow={workflowDetailed} />
         ) : (
           <div class="grid grid-cols-1 lg:grid-cols-12 gap-8">
             <div class="lg:col-span-8">
               <WorkflowDetail workflow={workflow} />
             </div>
             <aside class="lg:col-span-4">
               {/* Sidebar content for basic view */}
               <div class="bg-white dark:bg-gray-800 rounded-xl border border-gray-200 dark:border-gray-700 p-6 sticky top-8">
                 <h3 class="text-lg font-bold text-gray-900 dark:text-white mb-4">Workflow Info</h3>
                 <div class="space-y-4">
                   <div>
                     <p class="text-sm text-gray-500 dark:text-gray-400">Complexity</p>
                     <p class="text-base font-medium text-gray-900 dark:text-white capitalize">{workflow.complexityLevel}</p>
                   </div>
                   <div>
                     <p class="text-sm text-gray-500 dark:text-gray-400">Nodes</p>
                     <p class="text-base font-medium text-gray-900 dark:text-white">{workflow.nodes}</p>
                   </div>
                   {workflow.version && (
                     <div>
                       <p class="text-sm text-gray-500 dark:text-gray-400">n8n Version</p>
                       <p class="text-base font-medium text-gray-900 dark:text-white">{workflow.version}</p>
                     </div>
                   )}
                 </div>
               </div>
             </aside>
           </div>
         )}
       </div>

      {/* Related Workflows */}
      {relatedWorkflows.length > 0 && (
        <div class="mt-16">
          <h2 class="text-2xl font-bold text-gray-900 dark:text-white mb-8">Related Workflows</h2>
          <RelatedWorkflows workflows={relatedWorkflows} />
        </div>
      )}
    </div>
  </div>

  <script is:inline>
    // 动态加载 n8n webcomponent
    (function() {
      const workflowViewer = document.getElementById('workflow-viewer');
      const dataElement = workflowViewer?.querySelector('[data-workflow-json]');

      if (dataElement) {
        const workflowJson = dataElement.getAttribute('data-workflow-json');

        // 加载 n8n webcomponent 脚本
        const script = document.createElement('script');
        script.src = 'https://n8n-io.github.io/n8n-demo-webcomponent/n8n-demo.js';
        script.defer = true;

        script.onload = function() {
          // 创建 n8n demo iframe element
          const demoIframe = document.createElement('n8n-demo-iframe');
          demoIframe.setAttribute('workflow', workflowJson);
          demoIframe.style.display = 'block';
          demoIframe.style.width = '100%';
          demoIframe.style.height = '100%';

          // 替换占位元素
          dataElement.parentNode.replaceChild(demoIframe, dataElement);
        };

        document.head.appendChild(script);
      }
    })()"}

