  2 | /**
  3 |  * 工作流详情页（动态路由）
  4 |  * 展示单个工作流的完整信息
  5 |  */
  6 | 
  7 | import MainLayout from '../../layouts/MainLayout.astro';
  8 | import WorkflowDetail from '../../components/workflow/WorkflowDetail.astro';
  9 | import WorkflowDetailedView from '../../components/workflow/WorkflowDetailedView.astro';
 10 | import WorkflowMeta from '../../components/workflow/WorkflowMeta.astro';
 11 | import RelatedWorkflows from '../../components/workflow/RelatedWorkflows.astro';
 12 | import Breadcrumb from '../../components/ui/Breadcrumb.astro';
 13 | import { getWorkflows, getWorkflowBySlug, getWorkflowDetailedById } from '../../lib/services/workflows';
 14 | import { generateWorkflowSchema, generateBreadcrumbSchema, combineSchemas } from '../../lib/utils/schema';
 15 | import type { Workflow, WorkflowDetailed } from '../../lib/types/workflow';
 16 | 
 17 | // 生成静态路径
 18 | export async function getStaticPaths() {
 19 |   try {
 20 |     const result = await getWorkflows({ limit: 1000, offset: 0 });
 21 |     return result.workflows.map((workflow) => ({
 22 |       params: { slug: workflow.slug },
 23 |       props: { workflow },
 24 |     }));
 25 |   } catch (error) {
 26 |     console.error('Error generating workflow paths:', error);
 27 |     return [];
 28 |   }
 29 | }
 30 | 
 31 | const { slug } = Astro.params;
 32 | let { workflow } = Astro.props;
 33 | 
 34 | // 获取详细工作流信息（含结构化内容）
 35 | let workflowDetailed: WorkflowDetailed = workflow as WorkflowDetailed;
 36 | try {
 37 |   if (workflow.id) {
 38 |     const detailed = await getWorkflowDetailedById(workflow.id);
 39 |     if (detailed) {
 40 |       workflowDetailed = detailed;
 41 |     }
 42 |   }
 43 | } catch (error) {
 44 |   console.error('Error fetching detailed workflow:', error);
 45 |   // 降级到基础工作流信息
 46 | }
 47 | 
 48 | // 获取相关工作流（同分类或同作者）
 49 | let relatedWorkflows: Workflow[] = [];
 50 | try {
 51 |   if (workflow.categories && workflow.categories.length > 0) {
 52 |     const result = await getWorkflows({
 53 |       category: workflow.categories[0].toLowerCase().replace(/\s+/g, '-'),
 54 |       limit: 4,
 55 |       offset: 0,
 56 |     });
 57 |     // 过滤掉当前工作流
 58 |     relatedWorkflows = result.workflows.filter((w) => w.id !== workflow.id);
 59 |   }
 60 | } catch (error) {
 61 |   console.error('Error fetching related workflows:', error);
 62 | }
 63 | 
 64 | // 面包屑导航
 65 | const breadcrumbItems = [
 66 |   { name: 'Workflows', url: '/' },
 67 |   { name: workflow.title, url: `/workflows/${slug}` }
 68 | ];
 69 | 
 70 | const siteUrl = import.meta.env.PUBLIC_SITE_URL || 'https://n8nworkflow.com';
 71 | 
 72 | // 生成结构化数据
 73 | const workflowSchema = generateWorkflowSchema(workflow, siteUrl);
 74 | const breadcrumbSchema = generateBreadcrumbSchema(breadcrumbItems, siteUrl);
 75 | const combinedSchema = combineSchemas(workflowSchema, breadcrumbSchema);
 76 | 
 77 | // 读取工作流 JSON 数据
 78 | let workflowJson = null;
 79 | try {
 80 |   const workflowJsonPath = `/data/workflows/${slug}/${workflow.id}.json`;
 81 |   const response = await fetch(new URL(workflowJsonPath, Astro.url.origin));
 82 |   if (response.ok) {
 83 |     workflowJson = await response.json();
 84 |   }
 85 | } catch (error) {
 86 |   console.error('Error loading workflow JSON:', error);
 87 | }
 88 | 
 89 | // SEO 配置
 90 | const seoConfig = {
 91 |   title: `${workflow.title} - N8N Workflows`,
 92 |   description: workflow.description || `Download and use this ${workflow.complexityLevel} n8n workflow created by ${workflow.author.name}.`,
 93 |   canonical: `/workflows/${slug}`,
 94 |   image: workflow.featuredImage || workflow.thumbnail,
 95 |   type: 'article' as const,
 96 |   publishedTime: workflow.publishedAt,
 97 |   modifiedTime: workflow.updatedAt,
 98 |   author: workflow.author.name,
 99 |   tags: workflow.categories,
100 |   schema: combinedSchema,
101 | };
102 | 
