const delimiter = ', '; // change to '\n' if you prefer newlines

// Extract all webViewLinks from incoming items
const links = items
  .map(i => i.json?.webViewLink)
  .filter(Boolean);

// Optional: de-duplicate links in case upstream created duplicates
const uniqueLinks = Array.from(new Set(links));

// Create a single output item
return [
  {
    json: {
      photosCombined: uniqueLinks.join(delimiter),
      // If you also need arrays downstream:
      photosArray: uniqueLinks
    }
  }
];

const output = [];

for (const [key, value] of Object.entries(items[0].binary || {})) {
  // Get original extension if available, fallback to mime type-derived
  const originalName = value.fileName || 'unknown';
  const extension = originalName.includes('.')
    ? originalName.split('.').pop()
    : (value.mimeType?.split('/')[1] || 'bin');
  const fileName = originalName.includes('.')
    ? originalName
    : `unknown.${extension}`;

  output.push({
    binary: {
      data: value
    },
    json: {
      originalKey: key,
      fileName: fileName,
      mimeType: value.mimeType || 'unknown'
    }
  });
}

return output;

