// Input data assumed to be in items[0].json.custom_collections
const collections = items[0].json.custom_collections;

// Map only id and title to a new array of objects
const simplifiedCollections = collections.map(collection => {
  return {
    json: {
      id: collection.id,
      title: collection.title
    }
  };
});

return simplifiedCollections;

