// Parse Amplitude cohort webhook payload
const payload = $json;

// Extract first user from the batch (webhooks can send multiple)
const users = payload.users || [];

if (users.length === 0) {
  throw new Error('No users in webhook payload');
}

// Process each user in the batch
return users.map(user => {
  const email = user.user_id || user.user_properties?.email;

  if (!email) {
    throw new Error('No email found for user');
  }

  // Validate email
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailRegex.test(email)) {
    throw new Error(`Invalid email: ${email}`);
  }

  const domain = email.split('@')[1];
  const isFreeEmail = ['gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com'].includes(domain);

  return {
    json: {
      // User info
      email: email,
      name: user.user_properties?.name || user.user_properties?.full_name,
      userId: user.user_id,
      domain: domain,
      isFreeEmail: isFreeEmail,

      // Cohort context
      cohortName: payload.cohort_name,
      cohortId: payload.cohort_id,
      inCohort: payload.in_cohort,
      computedTime: payload.computed_time,

      // Usage data from Amplitude user properties
      usageData: {
        sessionCount: user.user_properties?.session_count || 0,
        lastSeen: user.user_properties?.last_seen,
        daysSinceSignup: user.user_properties?.days_since_signup,
        planType: user.user_properties?.plan_type || 'free',
        featuresUsed: user.user_properties?.features_used || [],
        teamSize: user.user_properties?.team_size || 1,
        apiCalls: user.user_properties?.api_calls_last_7d || 0,
        pricingPageViews: user.user_properties?.pricing_page_views || 0,
        teamInvitesSent: user.user_properties?.team_invites_sent || 0
      },

      timestamp: new Date().toISOString(),
      validationPassed: true
    }
  };
});

// MERGE USAGE + ENRICHMENT DATA
const inputs = $input.all();

const enrichedPQL = {
  user: {
    email: null,
    name: null,
    userId: null
  },
  usage: {},
  enrichment: {
    pdl: {},
    company: {}
  },
  cohort: {
    name: null,
    id: null
  },
  metadata: {
    enrichmentTimestamp: new Date().toISOString(),
    sourcesSuccessful: [],
    sourcesFailed: []
  }
};

inputs.forEach((input, index) => {
  const data = input.json;

  // Input 0: Company research
  if (index === 0 && data.choices?.[0]?.message?.content) {
    const content = data.choices[0].message.content;
    enrichedPQL.enrichment.company = {
      rawResearch: content,
      companyStage: extractSection(content, 'COMPANY STAGE'),
      techSophistication: extractSection(content, 'TECH SOPHISTICATION'),
      expansionSignals: extractSection(content, 'EXPANSION SIGNALS'),
      budgetIndicators: extractSection(content, 'BUDGET INDICATORS')
    };
    enrichedPQL.metadata.sourcesSuccessful.push('Company Research');
  }

  // Input 1: PDL data
  else if (index === 1 && data && !data.error) {
    const pdl = data.data || data;
    enrichedPQL.enrichment.pdl = {
      fullName: pdl.full_name,
      jobTitle: pdl.job_title,
      companyName: pdl.job_company_name,
      companySize: pdl.job_company_size,
      industry: pdl.job_company_industry,
      seniorityLevel: pdl.job_title_levels,
      linkedinUrl: pdl.linkedin_url,
      location: pdl.location_name
    };
    enrichedPQL.user.name = pdl.full_name;
    enrichedPQL.metadata.sourcesSuccessful.push('PDL');
  }

  // Input 2: Amplitude webhook data
  else if (index === 2 && data.validationPassed) {
    enrichedPQL.user.email = data.email;
    enrichedPQL.user.userId = data.userId;
    enrichedPQL.user.name = enrichedPQL.user.name || data.name;
    enrichedPQL.usage = data.usageData;
    enrichedPQL.cohort = {
      name: data.cohortName,
      id: data.cohortId
    };
    enrichedPQL.metadata.sourcesSuccessful.push('Amplitude');
  }
});

const qualityScore = calculateQuality(enrichedPQL);
enrichedPQL.metadata.dataQualityScore = qualityScore;

function extractSection(text, header) {
  const regex = new RegExp(`\\*\\*${header}:\\*\\*\\s*([^\\n*]+)`, 'i');
  const match = text.match(regex);
  return match ? match[1].trim() : 'N/A';
}

function calculateQuality(data) {
  let score = 0;
  if (data.enrichment.pdl.fullName) score += 25;
  if (data.usage.sessionCount > 0) score += 25;
  if (data.enrichment.company.companyStage !== 'N/A') score += 25;
  if (data.enrichment.pdl.companySize) score += 25;
  return score;
}

return [{ json: enrichedPQL }];
