const tagItems = $items('Get Tags');         // [{ json: { id, name, ... }}, ...]
const categoryItems = $items('Get Categories'); // [{ json: { id, name, ... }}, ...]
const postItems = $items('Get Posts');       // [{ json: { categories: [ids], tags: [ids], ... }}, ...]

// Build lookup maps
const tagsById = Object.fromEntries(
  tagItems
    .filter(i => i?.json?.id != null && i?.json?.name != null)
    .map(i => [String(i.json.id), i.json.name])
);

const categoriesById = Object.fromEntries(
  categoryItems
    .filter(i => i?.json?.id != null && i?.json?.name != null)
    .map(i => [String(i.json.id), i.json.name])
);

// Helper: normalize a field that might be number | string | array into an array of strings
const toIdArray = (val) => {
  if (val == null) return [];
  if (Array.isArray(val)) return val.map(v => String(v));
  return [String(val)];
};

// Enrich posts with names instead of IDs
const out = postItems.map(item => {
  const post = item.json;

  const categoryIds = toIdArray(post.categories);
  const tagIds = toIdArray(post.tags);

  const categoryNames = categoryIds
    .map(id => categoriesById[id])
    .filter(Boolean);

  const tagNames = tagIds
    .map(id => tagsById[id])
    .filter(Boolean);

  return {
    json: {
      ...post,
      categoryNames,
      tagNames,
    },
    // keep binary if present
    binary: item.binary,
  };
});

return out;
