const code = $('„Ç≥„Éº„ÉâÂÖ•Âäõ„Ç∑„Éº„ÉàÁõ£Ë¶ñ').item.json.code || '';
const errors = [];
if (code.includes('var ')) errors.push({type:'Major', msg:'Avoid var; use let/const.'});
if (code.includes('console.log')) errors.push({type:'Minor', msg:'Remove console.log in production.'});
const open = (code.match(/\{/g)||[]).length;
const close = (code.match(/\}/g)||[]).length;
if (open !== close) errors.push({type:'Critical', msg:`Brace imbalance: {=${open}} }=${close}`});
const score = Math.max(0, 10 - errors.length * 2);
return [{ json: { code, lintErrors: errors, lintScore: score }}];

const out = $json.output || '';
const lint = $('Lint Check').first().json || {};
const errors = lint.lintErrors || [];
const counts = { Critical:0, Major:0, Minor:0 };
errors.forEach(e=>{ if (counts[e.type]!==undefined) counts[e.type]++; });
let overall =  Number((lint.lintScore || 0));
const m = out.match(/\{\"overall_score\"\s*:\s*([0-9.]+)\}/);
if (m) overall = (overall + Number(m[1]))/2;
return [{ json: { reviewed: out, lintSummary: counts, lintScore: lint.lintScore||0, overallScore: Number(overall.toFixed(2)) } }];

const s = $json.lintSummary || {Critical:0, Major:0, Minor:0};
