const inputData = items[0].json.data;
let combined = {};
let questionCount = 1;

for (let key in inputData) {
    // Skip edit keys
    if (key.startsWith("If Question")) continue;

    // Main question
    const editKey = `If Question ${questionCount} needs editing, how should it be edited?`;

    combined[`Question ${questionCount}`] = key;                   // question text
    combined[`status ${questionCount}`] = inputData[key];          // status
    combined[`edit ${questionCount}`] = inputData[editKey] || "";  // edit suggestion

    questionCount++;
}

return [{ json: combined }];

