// Calculate performance metrics and identify patterns

const items = $input.all();
const results = items[0].json.results || [];

// Group ads by category and theme
const categoryPerformance = {};
const themePerformance = {};

results.forEach(result => {
  const adGroup = result.adGroup?.name || 'Unknown';
  const headlines = result.adGroupAd?.ad?.responsiveSearchAd?.headlines || [];
  const headline = headlines[0]?.text || '';
  const ctr = parseFloat(result.metrics?.ctr || 0);
  const impressions = parseInt(result.metrics?.impressions || 0);
  const clicks = parseInt(result.metrics?.clicks || 0);
  const conversions = parseFloat(result.metrics?.conversions || 0);
  const cost = parseFloat(result.metrics?.costMicros || 0) / 1000000;

  // Only include ads with sufficient data
  if (impressions < 100) return;

  // Aggregate by category (ad group)
  if (!categoryPerformance[adGroup]) {
    categoryPerformance[adGroup] = {
      total_impressions: 0,
      total_clicks: 0,
      total_conversions: 0,
      total_cost: 0,
      ad_count: 0
    };
  }

  categoryPerformance[adGroup].total_impressions += impressions;
  categoryPerformance[adGroup].total_clicks += clicks;
  categoryPerformance[adGroup].total_conversions += conversions;
  categoryPerformance[adGroup].total_cost += cost;
  categoryPerformance[adGroup].ad_count += 1;

  // Extract themes from headlines
  const themes = ['vegan', 'organic', 'natural', 'premium', 'budget', 'sale', 'new', 'bestseller', 'free shipping'];
  const headlineLower = headline.toLowerCase();

  themes.forEach(theme => {
    if (headlineLower.includes(theme)) {
      if (!themePerformance[theme]) {
        themePerformance[theme] = {
          total_impressions: 0,
          total_clicks: 0,
          total_conversions: 0,
          ad_count: 0
        };
      }
      themePerformance[theme].total_impressions += impressions;
      themePerformance[theme].total_clicks += clicks;
      themePerformance[theme].total_conversions += conversions;
      themePerformance[theme].ad_count += 1;
    }
  });
});

// Calculate averages
const categoryMetrics = Object.entries(categoryPerformance).map(([category, data]) => ({
  category,
  avg_ctr: ((data.total_clicks / data.total_impressions) * 100).toFixed(2),
  total_impressions: data.total_impressions,
  total_clicks: data.total_clicks,
  conversion_rate: data.total_clicks > 0 ? ((data.total_conversions / data.total_clicks) * 100).toFixed(2) : '0.00',
  avg_cost_per_click: data.total_clicks > 0 ? (data.total_cost / data.total_clicks).toFixed(2) : '0.00',
  ad_count: data.ad_count
}));

const themeMetrics = Object.entries(themePerformance).map(([theme, data]) => ({
  theme,
  avg_ctr: ((data.total_clicks / data.total_impressions) * 100).toFixed(2),
  total_impressions: data.total_impressions,
  total_clicks: data.total_clicks,
  conversion_rate: data.total_clicks > 0 ? ((data.total_conversions / data.total_clicks) * 100).toFixed(2) : '0.00',
  ad_count: data.ad_count
}));

// Sort by CTR
categoryMetrics.sort((a, b) => parseFloat(b.avg_ctr) - parseFloat(a.avg_ctr));
themeMetrics.sort((a, b) => parseFloat(b.avg_ctr) - parseFloat(a.avg_ctr));

// Identify top 20% and bottom 20%
const topCategories = categoryMetrics.slice(0, Math.max(1, Math.ceil(categoryMetrics.length * 0.2)));
const bottomCategories = categoryMetrics.slice(-Math.max(1, Math.ceil(categoryMetrics.length * 0.2)));

const topThemes = themeMetrics.slice(0, Math.max(1, Math.ceil(themeMetrics.length * 0.2)));
const bottomThemes = themeMetrics.slice(-Math.max(1, Math.ceil(themeMetrics.length * 0.2)));

return {
  overall_metrics: {
    total_ads_analyzed: results.length,
    date_range: 'Last 30 days',
    analysis_date: new Date().toISOString()
  },
  category_performance: categoryMetrics,
  theme_performance: themeMetrics,
  top_performers: {
    categories: topCategories,
    themes: topThemes
  },
  bottom_performers: {
    categories: bottomCategories,
    themes: bottomThemes
  }
};

