// Generate performance report
const performanceData = $node['AI Performance Analysis'].json;
const metricsData = $node['Calculate Performance Metrics'].json;

const topThemes = metricsData.top_performers?.themes || [];
const bottomThemes = metricsData.bottom_performers?.themes || [];

const report = `# 30-Day Performance Optimization Report

## Executive Summary
Date: ${new Date().toISOString().split('T')[0]}
Analysis Period: Last 30 days
Ads Analyzed: ${metricsData.overall_metrics?.total_ads_analyzed || 0}

## Top Performing Themes
${topThemes.map((t, i) => `${i+1}. ${t.theme}: ${t.avg_ctr}% CTR (${t.ad_count} ads)`).join('\n')}

## Underperforming Themes
${bottomThemes.map((t, i) => `${i+1}. ${t.theme}: ${t.avg_ctr}% CTR (${t.ad_count} ads)`).join('\n')}

## AI Insights
${performanceData.output || performanceData.insights || 'Analysis complete'}

## Next Optimization Cycle
Scheduled for: ${new Date(Date.now() + 30*24*60*60*1000).toISOString().split('T')[0]}
`;

return {
  report_text: report,
  report_html: report.replace(/\n/g, '<br>'),
  generated_at: new Date().toISOString(),
  top_themes: topThemes,
  bottom_themes: bottomThemes
};
