// Data quality checks for Shopping feed

const items = $input.all();
const qualityIssues = [];
const validProducts = [];

items.forEach(item => {
  const product = item.json;
  const issues = [];

  // Check for missing GTIN
  if (!product.gtin || product.gtin === '') {
    issues.push('Missing GTIN');
  }

  // Check image
  if (!product.image_link || product.image_link === '') {
    issues.push('Missing image link');
  }

  // Check for missing category
  if (!product.google_product_category || product.google_product_category === '') {
    issues.push('Missing Google product category');
  }

  // Check for pricing errors
  const price = parseFloat(product.price);
  if (isNaN(price) || price <= 0) {
    issues.push('Invalid price');
  }

  // Check title length (30-150 chars optimal)
  const title = product.title || '';
  if (title.length < 30) {
    issues.push('Title too short (recommend 30-150 chars)');
  }
  if (title.length > 150) {
    issues.push('Title too long (max 150 chars)');
  }

  // Check for missing description
  if (!product.description || product.description.length < 160) {
    issues.push('Description missing or too short (recommend 160-500 chars)');
  }

  // Check availability
  if (!product.availability || !['in stock', 'out of stock', 'preorder', 'backorder'].includes(product.availability.toLowerCase())) {
    issues.push('Invalid availability status');
  }

  // Quality score
  const maxIssues = 7;
  const qualityScore = Math.round(((maxIssues - issues.length) / maxIssues) * 100);

  const result = {
    ...product,
    quality_score: qualityScore,
    quality_issues: issues,
    needs_optimization: issues.length > 0
  };

  if (qualityScore < 80) {
    qualityIssues.push(result);
  } else {
    validProducts.push(result);
  }
});

return {
  total_products: items.length,
  products_with_issues: qualityIssues.length,
  valid_products: validProducts.length,
  all_products: [...validProducts, ...qualityIssues]
};

// Assign custom labels for campaign segmentation

const product = $input.item.json;
const price = parseFloat(product.price);
const cost = parseFloat(product.cost || 0);

// Calculate margin
const margin = cost > 0 ? ((price - cost) / price) * 100 : 0;

// Custom Label 0: Margin Tier
let marginTier = 'low';
if (margin > 40) marginTier = 'high';
else if (margin > 25) marginTier = 'mid';

// Custom Label 1: Performance
let performanceLabel = 'standard';
if (product.sales_rank && product.sales_rank < 100) {
  performanceLabel = 'bestseller';
} else if (product.created_date && new Date(product.created_date) > new Date(Date.now() - 30*24*60*60*1000)) {
  performanceLabel = 'new';
} else if (product.availability === 'limited stock') {
  performanceLabel = 'clearance';
}

// Custom Label 2: Seasonality
const month = new Date().getMonth() + 1;
let seasonality = 'evergreen';
if ([11, 12].includes(month)) seasonality = 'holiday';
else if ([6, 7, 8].includes(month)) seasonality = 'summer';
else if ([3, 4, 5].includes(month)) seasonality = 'spring';

// Custom Label 3: Stock Level
let stockLevel = 'in-stock';
if (product.quantity) {
  const qty = parseInt(product.quantity);
  if (qty === 0) stockLevel = 'out-of-stock';
  else if (qty < 10) stockLevel = 'low-stock';
  else if (qty > 100) stockLevel = 'high-stock';
}

// Custom Label 4: Category
const category = product.google_product_category || product.product_type || 'uncategorized';
const categoryShort = category.split('>')[0].trim().toLowerCase().replace(/\s+/g, '-');

return {
  ...product,
  optimized_title: $input.item.json.optimized_title || product.title,
  optimized_description: $input.item.json.optimized_description || product.description,
  custom_label_0: marginTier,
  custom_label_1: performanceLabel,
  custom_label_2: seasonality,
  custom_label_3: stockLevel,
  custom_label_4: categoryShort,
  margin_percent: margin.toFixed(2)
};

// Monitor for disapprovals and quality issues

const items = $input.all();
const products = items[0]?.json?.products || [];
const disapprovals = [];
const warnings = [];

products.forEach(product => {
  // Check for issues in NEW Merchant API format
  if (product.issues && product.issues.length > 0) {
    product.issues.forEach(issue => {
      if (issue.severity === 'error' || issue.applicability === 'DISAPPROVED') {
        disapprovals.push({
          product_id: product.offerId,
          product_title: product.title,
          issue: issue.description || issue.detail,
          severity: 'CRITICAL'
        });
      } else if (issue.severity === 'warning') {
        warnings.push({
          product_id: product.offerId,
          product_title: product.title,
          issue: issue.description || issue.detail,
          severity: 'WARNING'
        });
      }
    });
  }
});

return {
  total_products_checked: products.length,
  disapprovals: disapprovals,
  warnings: warnings,
  disapproval_count: disapprovals.length,
  warning_count: warnings.length,
  check_timestamp: new Date().toISOString()
};

