// Gets the body of the first item
const body = $input.item.json.body || {};

// Function to keep only numbers (removes spaces, parentheses, dashes etc.)
const onlyNumbers = (value) => {
  if (!value) return "";
  return String(value).replace(/\D+/g, "");
};

return [
  {
    json: {
      vacancy_id: body.vacancy_id || "",
      name: body.name || "",
      email: body.email || "",
      vacancy: body.vacancy || "",
      phone: onlyNumbers(body.phone),  // formatted phone
      birthday: body.birthday || "",
      location: body.location || ""
    }
  }
];

// Gets the content of the first item
const content = $input.item.json.content || "";

// Splits into parts by the "|" character and trims spaces
let parts = content.split('|').map(p => (p || "").trim());

// Ensures there are exactly 3 parts (no undefined)
while (parts.length < 3) {
  parts.push("");
}

// Safely extracts the parts
const [day1, day3, day7] = parts.slice(0, 3);

// Returns exactly in the requested format
return [
  {
    json: {
      day1,
      day3,
      day7
    }
  }
];

