format.schema:
={
  "type": "object",
  "properties": {
    "products": {
      "type": "array",
      "description": "List of e-commerce products found on the page extracted from the main grid or list of products.",
      "items": {
        "type": "object",
        "properties": {
          "title": {
            "type": "string",
            "description": "The product title or name"
          },
          "image_url": {
            "type": "string",
            "description": "The main product image URL. Look for the <img> tag within the product container and extract the full URL from the 'src' attribute. If the src is relative (starts with /), look for the absolute URL in 'data-src' or the first URL in 'srcset' attribute before any space or comma. Must be the complete URL as it appears in the HTML."
          }
        },
        "required": [
          "title",
          "image_url"
        ]
      }
    }
  },
  "required": [
    "products"
  ]
}
