// YOUR_AWS_SECRET_KEY_HERE====
// VERSIE 6: COMPLETE FIX + GUNNINGSCRITERIA + PLATFORM
// Node: "Splits Alle Velden"
// Toevoegingen: Gunningscriteria wegingen + Platform detectie
// YOUR_AWS_SECRET_KEY_HERE====

// Extract data sources from aggregated input
const aggregatedData = $json.allData || [];

console.log(`üìä Aggregate bevat ${aggregatedData.length} items`);

// Initialize data containers
let xmlData = {};
let publicatiesData = {};
let jsonDetail = null;

// Separate the three data sources
for (const item of aggregatedData) {
  // Check XML data
  if (item.PriorInformationNotice || item.ContractNotice || item.ContractAwardNotice) {
    xmlData = item.PriorInformationNotice || item.ContractNotice || item.ContractAwardNotice || {};
    console.log('‚úÖ XML data gevonden');
  }
  // Check publicaties data  
  else if (item.publicaties) {
    publicatiesData = item.publicaties;
    console.log('‚úÖ Publicaties data gevonden');
  }
  // Check JSON detail data
  else if (item.publicatieId || item.kenmerk || item.pbNummerTed) {
    jsonDetail = item;
    console.log('‚úÖ JSON detail data gevonden');
  }
}

// YOUR_AWS_SECRET_KEY_HERE====
// FLATTEN FUNCTIE
// YOUR_AWS_SECRET_KEY_HERE====
function flattenXML(obj, prefix = '') {
  let result = {};
  for (const key in obj) {
    const value = obj[key];
    const newKey = prefix ? `${prefix}_${key}` : key;

    if (value === null || value === undefined) {
      result[newKey] = null;
    } else if (typeof value === 'object' && !Array.isArray(value)) {
      Object.assign(result, flattenXML(value, newKey));
    } else if (Array.isArray(value)) {
      result[newKey] = value;
    } else {
      result[newKey] = value;
    }
  }
  return result;
}

// Flatten XML & Publicaties
const alleXMLVelden = flattenXML(xmlData, 'xml');

// YOUR_AWS_SECRET_KEY_HERE====
// FIX 1: NOTE VELD (CORRECT PAD)
// YOUR_AWS_SECRET_KEY_HERE====
const cbcNote = alleXMLVelden['xml_cac:ProcurementProject_cbc:Note__'] || '';
console.log(`‚úÖ Note veld: ${cbcNote ? 'GEVONDEN' : 'NIET GEVONDEN'}`);

// YOUR_AWS_SECRET_KEY_HERE====
// FIX 2: DESCRIPTIONS (HOOFD + ALLE PERCELEN)
// YOUR_AWS_SECRET_KEY_HERE====
const hoofdDescription = alleXMLVelden['xml_cac:ProcurementProject_cbc:Description__'] || '';
const lots = alleXMLVelden['xml_cac:ProcurementProjectLot'] || [];

let cbcDescription = hoofdDescription;
let perceelInfoArray = [];

if (Array.isArray(lots) && lots.length > 0) {
  console.log(`‚úÖ Aantal percelen: ${lots.length}`);

  lots.forEach((lot, index) => {
    const perceelProj = lot?.['cac:ProcurementProject'] || {};
    const perceelNaam = perceelProj?.['cbc:Name']?.['_'] || `Perceel ${index + 1}`;
    const perceelDesc = perceelProj?.['cbc:Description']?.['_'] || '';
    const perceelId = lot?.['cbc:ID']?.['_'] || '';

    // Duration
    const durationObj = perceelProj?.['cac:PlannedPeriod']?.['cbc:DurationMeasure'];
    const perceelDuur = durationObj?.['_'] || '';
    const perceelDuurEenheid = durationObj?.['unitCode'] || '';

    // CPV codes per perceel
    const mainCpv = perceelProj?.['cac:MainCommodityClassification']?.['cbc:ItemClassificationCode']?.['_'] || '';
    const addCpv = perceelProj?.['cac:AdditionalCommodityClassification']?.['cbc:ItemClassificationCode']?.['_'] || '';

    // Store perceel info
    perceelInfoArray.push({
      id: perceelId,
      naam: perceelNaam,
      beschrijving: perceelDesc,
      duur: perceelDuur,
      duurEenheid: perceelDuurEenheid,
      cpvMain: mainCpv,
      cpvAdditional: addCpv
    });

    // Add to combined description
    if (perceelDesc) {
      cbcDescription += `\n\n=== ${perceelNaam} ===\n${perceelDesc}`;
    }

    console.log(`   Perceel ${index + 1}: ${perceelNaam} (${perceelDuur} ${perceelDuurEenheid})`);
  });
}

// YOUR_AWS_SECRET_KEY_HERE====
// FIX 3: GESCHATTE WAARDE
// YOUR_AWS_SECRET_KEY_HERE====
const geschatteWaarde = alleXMLVelden['xml_cac:ProcurementProject_cac:RequestedTenderTotal_cbc:EstimatedOverallContractAmount__'] || '';
const geschatteWaardeCurrency = alleXMLVelden['xml_cac:ProcurementProject_cac:RequestedTenderTotal_cbc:EstimatedOverallContractAmount_currencyID'] || 'EUR';

let geschatteWaardeFormatted = '';
if (geschatteWaarde) {
  const bedrag = parseFloat(geschatteWaarde);
  if (!isNaN(bedrag)) {
    geschatteWaardeFormatted = `‚Ç¨${bedrag.toLocaleString('nl-NL')}`;
  }
}
console.log(`‚úÖ Geschatte waarde: ${geschatteWaardeFormatted || 'Niet opgegeven'}`);

// YOUR_AWS_SECRET_KEY_HERE====
// FIX 4: DEADLINE VOOR VRAGEN
// YOUR_AWS_SECRET_KEY_HERE====
let deadlineVragen = '';
let deadlineVragenTime = '';

if (Array.isArray(lots) && lots.length > 0) {
  const firstLot = lots[0];
  const infoRequestPeriod = firstLot?.['cac:TenderingProcess']?.['cac:AdditionalInformationRequestPeriod'];
  deadlineVragen = infoRequestPeriod?.['cbc:EndDate'] || '';
  deadlineVragenTime = infoRequestPeriod?.['cbc:EndTime'] || '';
}

const deadlineVragenFull = deadlineVragen && deadlineVragenTime 
  ? `${deadlineVragen} ${deadlineVragenTime}` 
  : deadlineVragen;

console.log(`‚úÖ Deadline vragen: ${deadlineVragenFull || 'Niet opgegeven'}`);

// YOUR_AWS_SECRET_KEY_HERE====
// FIX 5: RAAMOVEREENKOMST TYPE
// YOUR_AWS_SECRET_KEY_HERE====
let raamovereenkomstType = '';
if (Array.isArray(lots) && lots.length > 0) {
  const firstLot = lots[0];
  const contractingSystems = firstLot?.['cac:TenderingProcess']?.['cac:ContractingSystem'];
  if (Array.isArray(contractingSystems)) {
    const faSystem = contractingSystems.find(cs => 
      cs?.['cbc:ContractingSystemTypeCode']?.['listName'] === 'framework-agreement'
    );
    raamovereenkomstType = faSystem?.['cbc:ContractingSystemTypeCode']?.['_'] || '';
  }
}
console.log(`‚úÖ Raamovereenkomst type: ${raamovereenkomstType || 'Niet van toepassing'}`);

// YOUR_AWS_SECRET_KEY_HERE====
// FIX 6: CONTACT INFORMATIE
// YOUR_AWS_SECRET_KEY_HERE====
const orgData = alleXMLVelden['xml_ext:UBLExtensions_ext:UBLExtension_ext:ExtensionContent_efext:EformsExtension_efac:Organizations_efac:Organization_efac:Company'] || {};

const contactNaam = orgData?.['cac:Contact_cbc:Name'] || '';
const contactTelefoon = orgData?.['cac:Contact_cbc:Telephone'] || '';
const contactEmail = orgData?.['cac:Contact_cbc:ElectronicMail'] || '';
const organisatieWebsite = orgData?.['cbc:WebsiteURI'] || '';

console.log(`‚úÖ Contact: ${contactNaam || 'Niet opgegeven'}`);

// YOUR_AWS_SECRET_KEY_HERE====
// FIX 7: RECHTBANK / REVIEW BODY
// YOUR_AWS_SECRET_KEY_HERE====
const touchpointData = alleXMLVelden['xml_ext:UBLExtensions_ext:UBLExtension_ext:ExtensionContent_efext:EformsExtension_efac:Organizations_efac:Organization_efac:TouchPoint'] || {};

const reviewBodyNaam = touchpointData?.['cac:PartyName_cbc:Name__'] || '';
const reviewBodyEmail = touchpointData?.['cac:Contact_cbc:ElectronicMail'] || '';
const reviewBodyTelefoon = touchpointData?.['cac:Contact_cbc:Telephone'] || '';

// YOUR_AWS_SECRET_KEY_HERE====
// FIX 8: GERELATEERDE PUBLICATIES
// YOUR_AWS_SECRET_KEY_HERE====
let gerelateerdePublicatiesText = '';
let gerelateerdePublicatiesIds = [];

if (jsonDetail?.gerelateerdePublicaties && Array.isArray(jsonDetail.gerelateerdePublicaties)) {
  jsonDetail.gerelateerdePublicaties.forEach(pub => {
    gerelateerdePublicatiesIds.push(pub.publicatieId);
    const pubDate = pub.publicatieDatum ? pub.publicatieDatum.split('T')[0] : '';
    gerelateerdePublicatiesText += `${pub.typePublicatie} (ID: ${pub.publicatieId}, Datum: ${pubDate})\n`;
  });
  console.log(`‚úÖ Gerelateerde publicaties: ${gerelateerdePublicatiesIds.length}`);
}

// YOUR_AWS_SECRET_KEY_HERE====
// FIX 9: UBL METADATA
// YOUR_AWS_SECRET_KEY_HERE====
const ublVersionId = alleXMLVelden['xml_cbc:UBLVersionID'] || '';
const customizationId = alleXMLVelden['xml_cbc:CustomizationID'] || '';
const noticeId = alleXMLVelden['xml_cbc:ID__'] || '';
const issueDate = alleXMLVelden['xml_cbc:IssueDate'] || '';
const plannedDate = alleXMLVelden['xml_cbc:PlannedDate'] || '';

// YOUR_AWS_SECRET_KEY_HERE====
// NIEUW: GUNNINGSCRITERIA WEGINGEN
// YOUR_AWS_SECRET_KEY_HERE====

let gunningsCriteriaText = '';
let criteriaArray = [];

if (Array.isArray(lots) && lots.length > 0) {
  lots.forEach((lot, lotIndex) => {
    const tenderingTerms = lot?.['cac:TenderingTerms'];
    const awardingTerms = tenderingTerms?.['cac:AwardingTerms'];
    const awardingCriteria = awardingTerms?.['cac:AwardingCriterion'];

    if (awardingCriteria) {
      const criteriaList = Array.isArray(awardingCriteria) ? awardingCriteria : [awardingCriteria];
      const perceelNaam = lot?.['cac:ProcurementProject']?.['cbc:Name']?.['_'] || `Perceel ${lotIndex + 1}`;

      gunningsCriteriaText += `\n=== ${perceelNaam} ===\n`;

      criteriaList.forEach(criterion => {
        const description = criterion?.['cbc:Description']?.['_'] || '';
        const weight = criterion?.['cbc:Weight']?.['_'] || criterion?.['cbc:WeightNumeric']?.['_'] || '';
        const calculationExpression = criterion?.['cbc:CalculationExpression']?.['_'] || '';

        // Sub-criteria
        const subCriteria = criterion?.['cac:SubordinateAwardingCriterion'];

        if (description || weight) {
          const criteriaLine = weight 
            ? `${description}: ${weight}%` 
            : description;

          gunningsCriteriaText += `‚Ä¢ ${criteriaLine}\n`;

          criteriaArray.push({
            perceel: perceelNaam,
            criterium: description,
            weging: weight,
            berekening: calculationExpression
          });
        }

        // Process sub-criteria
        if (subCriteria) {
          const subList = Array.isArray(subCriteria) ? subCriteria : [subCriteria];
          subList.forEach(subCrit => {
            const subDesc = subCrit?.['cbc:Description']?.['_'] || '';
            const subWeight = subCrit?.['cbc:Weight']?.['_'] || subCrit?.['cbc:WeightNumeric']?.['_'] || '';

            if (subDesc || subWeight) {
              const subLine = subWeight 
                ? `${subDesc}: ${subWeight}%` 
                : subDesc;

              gunningsCriteriaText += `  - ${subLine}\n`;

              criteriaArray.push({
                perceel: perceelNaam,
                criterium: `${description} > ${subDesc}`,
                weging: subWeight,
                parent: description
              });
            }
          });
        }
      });
    }
  });
}

// Als er geen criteria in percelen staan, zoek op hoofdniveau
if (!gunningsCriteriaText) {
  const mainTenderingTerms = alleXMLVelden['xml_cac:TenderingTerms'];
  const mainAwardingTerms = mainTenderingTerms?.['cac:AwardingTerms'];
  const mainCriteria = mainAwardingTerms?.['cac:AwardingCriterion'];

  if (mainCriteria) {
    const criteriaList = Array.isArray(mainCriteria) ? mainCriteria : [mainCriteria];
    gunningsCriteriaText = '=== Gunningscriteria ===\n';

    criteriaList.forEach(criterion => {
      const description = criterion?.['cbc:Description']?.['_'] || '';
      const weight = criterion?.['cbc:Weight']?.['_'] || criterion?.['cbc:WeightNumeric']?.['_'] || '';

      if (description || weight) {
        const criteriaLine = weight 
          ? `${description}: ${weight}%` 
          : description;

        gunningsCriteriaText += `‚Ä¢ ${criteriaLine}\n`;

        criteriaArray.push({
          criterium: description,
          weging: weight
        });
      }
    });
  }
}

console.log(`‚úÖ Gunningscriteria: ${criteriaArray.length} criteria gevonden`);

// YOUR_AWS_SECRET_KEY_HERE====
// NIEUW: PLATFORM DETECTIE
// YOUR_AWS_SECRET_KEY_HERE====

let platform = 'TenderNed'; // Default
let platformUrl = '';

// Check de URL uit JSON detail
if (jsonDetail?.urlTsenderWebsite) {
  platformUrl = jsonDetail.urlTsenderWebsite.toLowerCase();

  if (platformUrl.includes('mercell.com') || platformUrl.includes('opic.com')) {
    platform = 'Mercell';
  } else if (platformUrl.includes('negometrix.com')) {
    platform = 'Negometrix';
  } else if (platformUrl.includes('ted.europa.eu')) {
    platform = 'TED (Europa)';
  } else if (platformUrl.includes('tenderned.nl')) {
    platform = 'TenderNed';
  } else if (platformUrl.includes('pianoo.nl')) {
    platform = 'PIANOo';
  } else if (platformUrl) {
    // Onbekend platform maar wel een URL
    platform = 'Overig';
  }
}

console.log(`‚úÖ Platform gedetecteerd: ${platform}`);

// YOUR_AWS_SECRET_KEY_HERE====
// EXTRACT CPV CODES (BESTAANDE FUNCTIONALITEIT)
// YOUR_AWS_SECRET_KEY_HERE====
const cpvCodes = [];
for (const key in alleXMLVelden) {
  if (key.includes('ItemClassificationCode__') && alleXMLVelden[key]) {
    cpvCodes.push(alleXMLVelden[key]);
  }
}
const uniqueCpvCodes = [...new Set(cpvCodes)];

// YOUR_AWS_SECRET_KEY_HERE====
// PROCESS JSON DETAIL VELDEN (BESTAAND)
// YOUR_AWS_SECRET_KEY_HERE====
let json_trefwoorden = '-';
let json_hoofdOpdracht = '';
let json_bijkomendeOpdrachten = '';
let json_nutsCode = '';

if (jsonDetail) {
  // Trefwoorden
  const trefwoorden = [
    jsonDetail.trefwoord1?.replace(/\"/g, ''),
    jsonDetail.trefwoord2?.replace(/\"/g, ''),
    jsonDetail.trefwoord3?.replace(/\"/g, '')
  ].filter(Boolean);
  json_trefwoorden = trefwoorden.length > 0 ? trefwoorden.join(', ') : '-';

  // CPV codes met omschrijvingen
  if (jsonDetail.cpvCodes && Array.isArray(jsonDetail.cpvCodes)) {
    const hoofdCpv = jsonDetail.cpvCodes.find(c => c.isHoofdOpdracht);
    const bijkomendeCpvs = jsonDetail.cpvCodes.filter(c => !c.isHoofdOpdracht);
    json_hoofdOpdracht = hoofdCpv ? `${hoofdCpv.code} ${hoofdCpv.omschrijving}` : '';
    json_bijkomendeOpdrachten = bijkomendeCpvs.length > 0 ? bijkomendeCpvs.map(c => `${c.code} ${c.omschrijving}`).join(', ') : '';
  }

  // NUTS code
  if (jsonDetail.nutsCodes && Array.isArray(jsonDetail.nutsCodes) && jsonDetail.nutsCodes.length > 0) {
    json_nutsCode = jsonDetail.nutsCodes[0].omschrijving || '';
  }
}

// YOUR_AWS_SECRET_KEY_HERE====
// GET PUBLICATIE ID FOR URLs
// YOUR_AWS_SECRET_KEY_HERE====
const publicatieId = jsonDetail?.publicatieId || publicatiesData.publicatieId || '';
const correctTenderNedUrl = `https://www.tenderned.nl/aankondigingen/overzicht/${publicatieId}`;
const originalLink = jsonDetail?.urlTsenderWebsite || correctTenderNedUrl;

// YOUR_AWS_SECRET_KEY_HERE====
// LOGGING
// YOUR_AWS_SECRET_KEY_HERE====
console.log(`‚úÖ CPV codes: ${uniqueCpvCodes.join(', ')}`);
console.log(`‚úÖ Trefwoorden: ${json_trefwoorden}`);
console.log(`‚úÖ Beschrijving (inclusief percelen): ${cbcDescription.length} tekens`);
console.log(`‚úÖ Note: ${cbcNote.length} tekens`);

// YOUR_AWS_SECRET_KEY_HERE====
// RETURN ALLE VELDEN (INCLUSIEF NIEUWE)
// YOUR_AWS_SECRET_KEY_HERE====
return {
  json: {
    // Alle platte XML velden
    ...alleXMLVelden,

    // CPV codes
    cpv_codes: uniqueCpvCodes,
    cpv_main: uniqueCpvCodes[0] || null,
    cpv_all_text: uniqueCpvCodes.join(', '),

    // JSON detail fields (basis)
    json_publicatieId: publicatieId,
    json_kenmerk: jsonDetail?.kenmerk || null,
    json_referentieNummer: jsonDetail?.referentieNummer || '',
    json_aanbestedingNaam: jsonDetail?.aanbestedingNaam || publicatiesData.aanbestedingNaam || '',
    json_opdrachtgeverNaam: jsonDetail?.opdrachtgeverNaam || publicatiesData.opdrachtgeverNaam || '',
    json_opdrachtBeschrijving: jsonDetail?.opdrachtBeschrijving || publicatiesData.opdrachtBeschrijving || '',
    json_publicatieDatum: jsonDetail?.publicatieDatum || publicatiesData.publicatieDatum || '',
    json_sluitingsDatum: jsonDetail?.sluitingsDatum || publicatiesData.sluitingsDatum || '',
    json_pbNummerTed: jsonDetail?.pbNummerTed || '',
    json_juridischKader: jsonDetail?.juridischKaderCode?.omschrijving || '',
    json_typeOpdracht: jsonDetail?.typeOpdrachtCode?.omschrijving || '',
    json_aardOpdracht: jsonDetail?.opdrachtAardCode?.omschrijving || '',
    json_procedure: jsonDetail?.procedureCode?.omschrijving || '',
    json_europeesNationaal: jsonDetail?.nationaalOfEuropeesCode?.omschrijving || '',
    json_trefwoorden: json_trefwoorden,
    json_hoofdOpdracht: json_hoofdOpdracht,
    json_bijkomendeOpdrachten: json_bijkomendeOpdrachten,
    json_nutsCode: json_nutsCode,

    // FIX 1: Gecorrigeerd Note veld
    xml_cbcNote: cbcNote,

    // FIX 2: Gecombineerde beschrijving (hoofd + percelen)
    xml_cbcDescription: cbcDescription,

    // FIX 3: Geschatte waarde
    xml_geschatteWaarde: geschatteWaarde,
    xml_geschatteWaardeCurrency: geschatteWaardeCurrency,
    xml_geschatteWaardeFormatted: geschatteWaardeFormatted,

    // FIX 4: Deadline voor vragen
    xml_deadlineVragen: deadlineVragenFull,

    // FIX 5: Raamovereenkomst type
    xml_raamovereenkomstType: raamovereenkomstType,

    // FIX 6: Contact informatie
    xml_contactNaam: contactNaam,
    xml_contactTelefoon: contactTelefoon,
    xml_contactEmail: contactEmail,
    xml_organisatieWebsite: organisatieWebsite,

    // FIX 7: Review body (rechtbank)
    xml_reviewBodyNaam: reviewBodyNaam,
    xml_reviewBodyEmail: reviewBodyEmail,
    xml_reviewBodyTelefoon: reviewBodyTelefoon,

    // FIX 8: Gerelateerde publicaties
    json_gerelateerdePublicaties: gerelateerdePublicatiesText,
    json_gerelateerdePublicatiesIds: gerelateerdePublicatiesIds.join(', '),

    // FIX 9: UBL metadata
    xml_ublVersionId: ublVersionId,
    xml_customizationId: customizationId,
    xml_noticeId: noticeId,
    xml_issueDate: issueDate,
    xml_plannedDate: plannedDate,

    // NIEUW: Gunningscriteria wegingen
    xml_gunningsCriteria: gunningsCriteriaText,
    xml_criteriaArray: JSON.stringify(criteriaArray),
    xml_aantalCriteria: criteriaArray.length,

    // NIEUW: Platform detectie
    xml_platform: platform,
    xml_platformUrl: jsonDetail?.urlTsenderWebsite || '',

    // Perceel informatie (gestructureerd)
    percelen_aantal: perceelInfoArray.length,
    percelen_data: JSON.stringify(perceelInfoArray),
    perceel1_naam: perceelInfoArray[0]?.naam || '',
    perceel1_id: perceelInfoArray[0]?.id || '',
    perceel1_duur: perceelInfoArray[0]?.duur || '',
    perceel1_duurEenheid: perceelInfoArray[0]?.duurEenheid || '',
    perceel2_naam: perceelInfoArray[1]?.naam || '',
    perceel2_id: perceelInfoArray[1]?.id || '',
    perceel2_duur: perceelInfoArray[1]?.duur || '',
    perceel2_duurEenheid: perceelInfoArray[1]?.duurEenheid || '',

    // URLs
    originalLink: originalLink,
    tenderNedUrl: correctTenderNedUrl,

    // Timestamps
    verwerkingTimestamp: new Date().toISOString(),

    // Originele data backup
    originele_data_json: JSON.stringify({
      xml_data: xmlData,
      publicatie_data: publicatiesData,
      json_detail: jsonDetail,
      aggregated_raw: aggregatedData
    })
  }
};

// Verwerk de response en extract de publicaties
const response = $input.item.json;

// Spring Boot paginated response heeft 'content' veld
let publicaties = [];
if (response.content && Array.isArray(response.content)) {
  publicaties = response.content;
} else if (Array.isArray(response)) {
  publicaties = response;
}

console.log(`‚úÖ ${publicaties.length} nieuwe publicaties gevonden`);

if (publicaties.length === 0) {
  return {
    json: {
      aantalPublicaties: 0,
      publicaties: [],
      bericht: 'Geen nieuwe tenders gevonden'
    }
  };
}

// Return met array voor Split node
return {
  json: {
    aantalPublicaties: publicaties.length,
    publicaties: publicaties
  }
};
