// Parse Recruiter Output - Tie-breaker only (no bonus to fit_score)
try {
  let raw = $json.output || $json;

  // N·∫øu AI tr·∫£ v·ªÅ {output:"[...json...]"} th√¨ l·∫•y ph·∫ßn b√™n trong
  if (typeof raw === 'object' && raw.output) raw = raw.output;

  // L√†m s·∫°ch chu·ªói JSON n·∫øu c·∫ßn
  if (typeof raw === 'string') {
    const match = raw.match(/\[\s*{[\s\S]*}\s*\]/);
    if (match) raw = match[0];
    raw = raw.trim().replace(/^[\uFEFF\x00-\x1F]+/, '');
  }

  // Th·ª≠ parse JSON (k·ªÉ c·∫£ khi b·ªã double-encode)
  let parsed;
  try {
    parsed = typeof raw === 'string' ? JSON.parse(raw) : raw;
  } catch (innerErr) {
    parsed = JSON.parse(JSON.parse(raw));
  }

  // Chu·∫©n h√≥a th√†nh m·∫£ng
  const arr = Array.isArray(parsed) ? parsed : [parsed];
  if (!arr.length) throw new Error("Kh√¥ng c√≥ d·ªØ li·ªáu ·ª©ng vi√™n h·ª£p l·ªá");

  // Helpers
  const norm = (s) => (s || '').toLowerCase().trim();
  const domEq = (cv) => norm(cv.cv_domain) && norm(cv.jd_domain) && norm(cv.cv_domain) === norm(cv.jd_domain);
  const domNear = (cv) => {
    const a = norm(cv.cv_domain), b = norm(cv.jd_domain);
    if (!a || !b) return false;
    return a.includes(b) || b.includes(a);
  };
  const kwLen = (cv) => Array.isArray(cv.matched_keywords) ? cv.matched_keywords.length : 0;
  const years = (cv) => Number(cv.cv_years || 0);

  // ƒê·ªìng b·ªô nh√£n hi·ªÉn th·ªã v√† auto-fix domain_match n·∫øu AI sai
  const candidates = arr.map(cv => {
    const c = { ...cv };
    // Role match label = ƒë√∫ng domain CV (kh√¥ng ‚ÄúOther‚Äù c·ª©ng)
    c.role_match_label = (c.cv_domain || '').trim() || 'Kh√¥ng x√°c ƒë·ªãnh';

    // Auto-fix: n·∫øu domain b·∫±ng nhau m√† domain_match = false th√¨ s·ª≠a th√†nh true
    if (domEq(c) && c.domain_match === false) c.domain_match = true;

    return c;
  });

  // T·ªïng v√† danh s√°ch ƒë·∫°t chu·∫©n
  const total = candidates.length;
  const qualified = candidates.filter(cv => (cv.fit_score || 0) >= 70);

  // Comparator ch·ªçn ·ª©ng vi√™n t·ªët nh·∫•t:
  // 1) fit_score cao h∆°n th·∫Øng
  // 2) N·∫øu b·∫±ng nhau: domain = JD th·∫Øng
  // 3) N·∫øu v·∫´n b·∫±ng nhau: domain g·∫ßn JD th·∫Øng
  // 4) N·∫øu v·∫´n b·∫±ng nhau: nhi·ªÅu matched_keywords h∆°n th·∫Øng
  // 5) N·∫øu v·∫´n b·∫±ng nhau: cv_years nhi·ªÅu h∆°n th·∫Øng
  const better = (a, b) => {
    const fa = Number(a.fit_score || 0);
    const fb = Number(b.fit_score || 0);
    if (fa !== fb) return fa > fb ? a : b;

    const aEq = domEq(a), bEq = domEq(b);
    if (aEq !== bEq) return aEq ? a : b;

    const aNear = domNear(a), bNear = domNear(b);
    if (aNear !== bNear) return aNear ? a : b;

    const aKw = kwLen(a), bKw = kwLen(b);
    if (aKw !== bKw) return aKw > bKw ? a : b;

    const aY = years(a), bY = years(b);
    if (aY !== bY) return aY > bY ? a : b;

    return a; // gi·ªØ nguy√™n th·ª© t·ª± n·∫øu v·∫´n h√≤a
  };

  const best = candidates.reduce((acc, cv) => acc ? better(acc, cv) : cv, null);

  // Summary lu√¥n d√πng fit_score ‚Äúg·ªëc‚Äù (kh√¥ng c·ªông th∆∞·ªüng)
  const summary_text =
    best && (best.fit_score || 0) >= 70
      ? `üèÜ ·ª®ng vi√™n ƒëi·ªÉm ƒë√°nh gi√° cao nh·∫•t: ${best.candidate_name || "(Kh√¥ng x√°c ƒë·ªãnh)"} (${best.fit_score}%)`
      : `Kh√¥ng c√≥ h·ªì s∆° ph√π h·ª£p. ƒê·ªÅ xu·∫•t t√¨m th√™m ·ª©ng vi√™n kh√°c.`;

  return [{
    json: {
      total_candidates: total,
      qualified_candidates: qualified.length,
      best_candidate: best?.candidate_name || "",
      best_score: best?.fit_score || 0,
      summary_text,
      candidates
    }
  }];

} catch (err) {
  return [{
    json: {
      error: "JSON_PARSE_FAILED",
      message: err.message,
      raw_snippet: String($json.output || '').slice(0, 500)
    }
  }];
}

