// ====== H√ÄM TI·ªÜN √çCH ======
const normalize = (s) => (s || "").replace(/\s+/g, " ").trim();

const cleanName = (name) => {
  return name
    .replace(/[^\p{L}\s]/gu, "")
    .replace(/\s+/g, " ")
