    .replace(/\b(\p{L})/gu, (m) => m.toUpperCase());
};

return extracted.map((ex, i) => {
  const src = (srcItems[i] && srcItems[i].json) ? srcItems[i].json : {};
  const text = ex.json?.text || "";
  const lines = text.split(/\n/).map(l => l.trim()).filter(Boolean);
  const header = lines.slice(0, 5).join(" ");

  let candidateName = "";
  let candidatePosition = "";
  let roleMatchType = "Other";

  // ====== 1Ô∏è‚É£ X√ÅC ƒê·ªäNH T√äN ·ª®NG VI√äN ======
  // D·∫°ng ƒë·∫ßy ƒë·ªß: "Nguyen Thi Ngoc Phuong"
  const fullNameRegex = /\b([A-Zƒê][a-z√†√°·∫°·∫£√£√¢·∫ß·∫•·∫≠·∫©·∫´ƒÉ·∫±·∫Ø·∫∑·∫≥·∫µ√®√©·∫π·∫ª·∫Ω√™·ªÅ·∫ø·ªá·ªÉ·ªÖ√¨√≠·ªã·ªâƒ©√≤√≥·ªç·ªè√µ√¥·ªì·ªë·ªô·ªï·ªó∆°·ªù·ªõ·ª£·ªü·ª°√π√∫·ª•·ªß≈©∆∞·ª´·ª©·ª±·ª≠·ªØ·ª≥√Ω·ªµ·ª∑·ªπƒëƒê]{1,}\s){1,4}[A-Zƒê][a-z√†√°·∫°·∫£√£√¢·∫ß·∫•·∫≠·∫©·∫´ƒÉ·∫±·∫Ø·∫∑·∫≥·∫µ√®√©·∫π·∫ª·∫Ω√™·ªÅ·∫ø·ªá·ªÉ·ªÖ√¨√≠·ªã·ªâƒ©√≤√≥·ªç·ªè√µ√¥·ªì·ªë·ªô·ªï·ªó∆°·ªù·ªõ·ª£·ªü·ª°√π√∫·ª•·ªß≈©∆∞·ª´·ª©·ª±·ª≠·ªØ·ª≥√Ω·ªµ·ª∑·ªπƒëƒê]{2,}/gu;
  const matchName = header.match(fullNameRegex);
  if (matchName) candidateName = cleanName(matchName[0]);

  // D·∫°ng vi·∫øt t·∫Øt: N.T.K.Di·ªÖm, L.T.M.Trinh
  if (!candidateName) {
    const abbr = header.match(/[A-Z]\.[A-Z]\.[A-Z]\.[A-Za-z√Ä-·ªπ]{2,}/);
    if (abbr) candidateName = abbr[0].replace(/\./g, " ").trim();
  }

  // T√™n n·∫±m trong ‚ÄúTh√¥ng Tin C√° Nh√¢n‚Äù ho·∫∑c ‚ÄúH·ªç v√† T√™n‚Äù
  if (!candidateName) {
    const infoBlock = text.match(/(H·ªç\s*v√†\s*t√™n|Th√¥ng\s*Tin\s*C√°\s*Nh√¢n)[\s\S]{0,150}/i);
    if (infoBlock) {
      const found = infoBlock[0].match(fullNameRegex);
