  // ====== 2Ô∏è‚É£ X√ÅC ƒê·ªäNH CH·ª®C DANH (V·ªä TR√ç) ======
  const roleRegex = /(Team\s*Leader|Manager|Consultant|Specialist|Developer|Engineer|Officer|Supervisor|Coordinator|Executive|Director|Leader|Nh√¢n\s*vi√™n\s*[^\n,;]+|Chuy√™n\s*vi√™n\s*[^\n,;]+|Tr∆∞·ªüng\s*[^\n,;]+|Gi√°m\s*s√°t\s*[^\n,;]+)/i;
  const matchRole = text.match(roleRegex);
  if (matchRole) candidatePosition = normalize(matchRole[0]);

  // ====== 3Ô∏è‚É£ PH√ÇN LO·∫†I role_match_type ======
  if (/Manager|Director|Tr∆∞·ªüng/i.test(candidatePosition)) roleMatchType = "Manager";
  else if (/Consultant|Functional|Analyst|Specialist|Officer/i.test(candidatePosition)) roleMatchType = "Functional";
  else if (/Developer|Technical|Engineer|Programmer|Basis/i.test(candidatePosition)) roleMatchType = "Technical";
  else if (/Admin|H√†nh\s*ch√°nh|Nh√¢n\s*s·ª±/i.test(candidatePosition)) roleMatchType = "Admin";
  else roleMatchType = "Other";

  // ====== 4Ô∏è‚É£ K·∫æT QU·∫¢ ======
  return {
