function extractRawName(text = "") {
  if (!text) return "";

  const lines = text
    .replace(/\r/g, "")
    .replace(/\t/g, " ")
    .split("\n")
    .map(l => l.trim())
    .filter(l => l && l.length > 1)
    .slice(0, 8); // ch·ªâ xem v√†i d√≤ng ƒë·∫ßu ti√™n

  // B·ªè qua c√°c d√≤ng ch·ª©a c·ª•m t·ª´ ‚ÄúTh√¥ng tin c√° nh√¢n‚Äù, ‚ÄúCV‚Äù, ‚ÄúS∆° y·∫øu l√Ω l·ªãch‚Äù, ‚ÄúCurriculum Vitae‚Äù
  const skip = /(th√¥ng tin|s∆° y·∫øu|curriculum vitae|cv|profile)/i;
  const filtered = lines.filter(l => !skip.test(l));

  // L·∫•y d√≤ng ƒë·∫ßu ti√™n h·ª£p l·ªá (gi·ªØ nguy√™n format, kh√¥ng s·ª≠a g√¨)
  return filtered.length ? filtered[0] : "";
