// Kh√¥ng ƒë·ªïi hoa/th∆∞·ªùng, kh√¥ng b·ªè d·∫•u, kh√¥ng b·ªè d·∫•u ch·∫•m.

function extractRawName(text = "") {
  if (!text) return "";

