const items = $input.all();
const today = $now.toDate();
const threeDaysLater = new Date(today);
threeDaysLater.setDate(today.getDate() + 3);

const upcomingReviews = items.filter(item => {
  const reviewDate = new Date(item.json.ReviewDate);
  return reviewDate >= today && reviewDate <= threeDaysLater && item.json.Status !== 'Completed';
});

const todayReviews = upcomingReviews.filter(item => {
  const reviewDate = new Date(item.json.ReviewDate);
  return reviewDate.toDateString() === today.toDateString();
});

const reminderReviews = upcomingReviews.filter(item => {
  const reviewDate = new Date(item.json.ReviewDate);
  return reviewDate.toDateString() !== today.toDateString();
});

return {
  upcomingReviews: upcomingReviews,
  todayReviews: todayReviews,
  reminderReviews: reminderReviews
};

