// Process Inflows
const inflows = $input.first().json;
let totalInflow = 0;
const inflowsByCategory = {};

if (Array.isArray(inflows)) {
  inflows.forEach(transaction => {
    totalInflow += parseFloat(transaction.amount || 0);
    const category = transaction.category || 'Other';
    if (!inflowsByCategory[category]) {
      inflowsByCategory[category] = 0;
    }
    inflowsByCategory[category] += parseFloat(transaction.amount || 0);
  });
}

return {
  total_inflow: totalInflow.toFixed(2),
  inflow_categories: inflowsByCategory,
  inflow_count: Array.isArray(inflows) ? inflows.length : 0,
  date: new Date().toISOString().split('T')[0]
};

// Process Outflows
const outflows = $input.first().json;
let totalOutflow = 0;
const outflowsByCategory = {};

if (Array.isArray(outflows)) {
  outflows.forEach(transaction => {
    totalOutflow += parseFloat(transaction.amount || 0);
    const category = transaction.category || 'Other';
    if (!outflowsByCategory[category]) {
      outflowsByCategory[category] = 0;
    }
    outflowsByCategory[category] += parseFloat(transaction.amount || 0);
  });
}

return {
  total_outflow: totalOutflow.toFixed(2),
  outflow_categories: outflowsByCategory,
  outflow_count: Array.isArray(outflows) ? outflows.length : 0,
  date: new Date().toISOString().split('T')[0]
};

const data = $input.all();
const inflow = data[0].json;
const outflow = data[1].json;

const totalInflow = parseFloat(inflow.total_inflow || 0);
const totalOutflow = parseFloat(outflow.total_outflow || 0);
const netCashFlow = totalInflow - totalOutflow;

return {
  date: inflow.date,
  total_inflow: totalInflow.toFixed(2),
  total_outflow: totalOutflow.toFixed(2),
  net_cash_flow: netCashFlow.toFixed(2),
  inflow_categories: inflow.inflow_categories,
  outflow_categories: outflow.outflow_categories,
  inflow_count: inflow.inflow_count,
  outflow_count: outflow.outflow_count,
  cash_flow_status: netCashFlow >= 0 ? 'Positive' : 'Negative'
};

const data = $input.first().json;

// Build category breakdown HTML
let inflowHTML = '';
for (const [category, amount] of Object.entries(data.inflow_categories)) {
  inflowHTML += `<tr><td>${category}</td><td>$${parseFloat(amount).toFixed(2)}</td></tr>`;
}

let outflowHTML = '';
for (const [category, amount] of Object.entries(data.outflow_categories)) {
  outflowHTML += `<tr><td>${category}</td><td>$${parseFloat(amount).toFixed(2)}</td></tr>`;
}

const statusColor = data.cash_flow_status === 'Positive' ? 'green' : 'red';

const htmlReport = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; max-width: 800px; margin: 20px auto; }
    h1 { color: #333; border-bottom: 3px solid #4CAF50; padding-bottom: 10px; }
    h2 { color: #666; margin-top: 30px; }
    table { width: 100%; border-collapse: collapse; margin: 20px 0; }
    th, td { padding: 12px; text-align: left; border-bottom: 1px solid #ddd; }
    th { background-color: #4CAF50; color: white; }
    .summary { background: #f5f5f5; padding: 20px; border-radius: 8px; margin: 20px 0; }
    .positive { color: green; font-weight: bold; }
    .negative { color: red; font-weight: bold; }
  </style>
</head>
<body>
  <h1>Daily Cash Flow Report</h1>
  <p><strong>Date:</strong> ${data.date}</p>

  <div class="summary">
    <h2>Summary</h2>
    <p><strong>Total Inflows:</strong> $${data.total_inflow} (${data.inflow_count} transactions)</p>
    <p><strong>Total Outflows:</strong> $${data.total_outflow} (${data.outflow_count} transactions)</p>
    <p><strong>Net Cash Flow:</strong> <span style="color: ${statusColor};">$${data.net_cash_flow}</span></p>
    <p><strong>Status:</strong> <span style="color: ${statusColor};">${data.cash_flow_status}</span></p>
  </div>

  <h2>Cash Inflows by Category</h2>
  <table>
    <tr><th>Category</th><th>Amount</th></tr>
    ${inflowHTML}
    <tr style="background: #f0f0f0; font-weight: bold;"><td>Total</td><td>$${data.total_inflow}</td></tr>
  </table>

  <h2>Cash Outflows by Category</h2>
  <table>
    <tr><th>Category</th><th>Amount</th></tr>
    ${outflowHTML}
    <tr style="background: #f0f0f0; font-weight: bold;"><td>Total</td><td>$${data.total_outflow}</td></tr>
  </table>
</body>
</html>
`;

return {
  html_report: htmlReport,
  ...data
};

