const error = $input.first().json.error || 'Unknown API Error';
const timestamp = new Date().toISOString();

return [{
  json: {
    error_type: 'API_FAILURE',
    error_message: error,
    timestamp: timestamp,
    workflow_name: 'Financial Transaction Monitor',
    severity: 'HIGH'
  }
}];

const items = $input.all();
const enrichedTransactions = [];

for (const item of items) {
  const txn = item.json;

  const isWeekend = new Date(txn.timestamp).getDay() % 6 === 0;
  const isNightTime = new Date(txn.timestamp).getHours() < 6 || new Date(txn.timestamp).getHours() > 22;
  const isInternational = txn.country && txn.country !== 'US';

  let adjustedRiskScore = parseFloat(txn.risk_score || 0);

  if (isWeekend) adjustedRiskScore += 0.5;
  if (isNightTime) adjustedRiskScore += 0.8;
  if (isInternational) adjustedRiskScore += 1.2;
  if (txn.payment_method === 'wire_transfer') adjustedRiskScore += 1.0;

  let category = 'NORMAL';
  const amount = parseFloat(txn.amount);

  if (amount >= 50000 || adjustedRiskScore >= 9) {
    category = 'CRITICAL';
  } else if (amount >= 25000 || adjustedRiskScore >= 8) {
    category = 'HIGH';
  } else if (amount >= 10000 || adjustedRiskScore >= 7) {
    category = 'MEDIUM';
  } else if (adjustedRiskScore >= 5) {
    category = 'LOW_RISK';
  }

  const recentSimilarCount = txn.vendor_transaction_count_24h || 0;
  const isVelocityAnomaly = recentSimilarCount > 5;

  enrichedTransactions.push({
    json: {
      ...txn,
      original_risk_score: txn.risk_score,
      adjusted_risk_score: adjustedRiskScore.toFixed(2),
      risk_category: category,
      is_weekend: isWeekend,
      is_night_time: isNightTime,
      is_international: isInternational,
      velocity_anomaly: isVelocityAnomaly,
      processed_at: new Date().toISOString(),
      alert_priority: category === 'CRITICAL' ? 1 : category === 'HIGH' ? 2 : 3
    }
  });
}

return enrichedTransactions;

