const items = $input.all();

const criticalCount = items.filter(i => i.json.risk_category === 'CRITICAL').length;
const highCount = items.filter(i => i.json.risk_category === 'HIGH').length;
const mediumCount = items.filter(i => i.json.risk_category === 'MEDIUM').length;

const totalAmount = items.reduce((sum, i) => sum + parseFloat(i.json.amount || 0), 0);
const avgRiskScore = items.reduce((sum, i) => sum + parseFloat(i.json.adjusted_risk_score || 0), 0) / items.length;

const internationalCount = items.filter(i => i.json.is_international).length;
const velocityAnomalies = items.filter(i => i.json.velocity_anomaly).length;

return [{
  json: {
    summary_timestamp: new Date().toISOString(),
    total_transactions: items.length,
    critical_alerts: criticalCount,
    high_priority_alerts: highCount,
    medium_priority_alerts: mediumCount,
    total_amount: totalAmount.toFixed(2),
    average_risk_score: avgRiskScore.toFixed(2),
    international_transactions: internationalCount,
    velocity_anomalies: velocityAnomalies,
    requires_immediate_action: criticalCount > 0 || highCount > 2
  }
}];
