const invoices = $input.all();
const today = new Date();
const results = [];

for (const invoice of invoices) {
  const dueDate = new Date(invoice.json.due_date);
  const issueDate = new Date(invoice.json.issue_date);
  const lastReminder = invoice.json.last_reminder_sent ? new Date(invoice.json.last_reminder_sent) : null;

  // Calculate days overdue
  const daysOverdue = Math.floor((today - dueDate) / (1000 * 60 * 60 * 24));

  // Calculate days since last reminder
  const daysSinceReminder = lastReminder ? Math.floor((today - lastReminder) / (1000 * 60 * 60 * 24)) : 999;

  // Determine reminder type and urgency
  let reminderType = '';
  let urgencyLevel = '';
  let shouldSendReminder = false;

  if (daysOverdue > 30 && daysSinceReminder >= 3) {
    reminderType = 'final_notice';
    urgencyLevel = 'critical';
    shouldSendReminder = true;
  } else if (daysOverdue > 14 && daysSinceReminder >= 5) {
    reminderType = 'second_reminder';
    urgencyLevel = 'high';
    shouldSendReminder = true;
  } else if (daysOverdue > 0 && daysSinceReminder >= 7) {
    reminderType = 'first_reminder';
    urgencyLevel = 'medium';
    shouldSendReminder = true;
  } else if (daysOverdue === 0) {
    reminderType = 'due_today';
    urgencyLevel = 'medium';
    shouldSendReminder = true;
  } else if (daysOverdue < 0 && daysOverdue >= -3 && !lastReminder) {
    reminderType = 'upcoming_reminder';
    urgencyLevel = 'low';
    shouldSendReminder = true;
  }

  if (shouldSendReminder) {
    results.push({
      json: {
        ...invoice.json,
        daysOverdue,
        daysSinceReminder,
        reminderType,
        urgencyLevel,
        shouldSendReminder
      }
    });
  }
}

return results;

