const items = $input.all();
const results = [];

for (const item of items) {
  const aiResponse = item.json.message?.content || item.json.response || '';

  // Extract subject and body from AI response
  const subjectMatch = aiResponse.match(/Subject:(.+?)\n/i);
  const subject = subjectMatch ? subjectMatch[1].trim() : `Payment Reminder - Invoice ${item.json.invoice_number}`;

  // Remove subject line from body
  let body = aiResponse.replace(/Subject:(.+?)\n/i, '').trim();

  // Add invoice details table
  const invoiceTable = `
  <div style="background-color: #f5f5f5; padding: 20px; border-radius: 8px; margin: 20px 0;">
    <h3 style="color: #333; margin-top: 0;">Invoice Details</h3>
    <table style="width: 100%; border-collapse: collapse;">
      <tr>
        <td style="padding: 8px; font-weight: bold;">Invoice Number:</td>
        <td style="padding: 8px;">${item.json.invoice_number}</td>
      </tr>
      <tr>
        <td style="padding: 8px; font-weight: bold;">Amount:</td>
        <td style="padding: 8px;">${item.json.currency} ${item.json.invoice_amount}</td>
      </tr>
      <tr>
        <td style="padding: 8px; font-weight: bold;">Due Date:</td>
        <td style="padding: 8px;">${item.json.due_date}</td>
      </tr>
      <tr>
        <td style="padding: 8px; font-weight: bold;">Status:</td>
        <td style="padding: 8px; color: ${item.json.daysOverdue > 0 ? '#d32f2f' : '#f57c00'};">
          ${item.json.daysOverdue > 0 ? `Overdue by ${item.json.daysOverdue} days` : 'Due Soon'}
        </td>
      </tr>
    </table>
  </div>
  `;

  // Convert body to HTML format
  body = body.replace(/\n/g, '<br>').replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>');

  // Combine into HTML email
  const htmlBody = `
  <html>
    <body style="font-family: Arial, sans-serif; line-height: 1.6; color: #333;">
      ${body.split('<br>')[0]}
      ${invoiceTable}
      ${body.split('<br>').slice(1).join('<br>')}
      <div style="margin-top: 30px; padding-top: 20px; border-top: 1px solid #ddd; font-size: 12px; color: #666;">
        <p>This is an automated reminder. If you have already made the payment, please disregard this email.</p>
      </div>
    </body>
  </html>
  `;

  results.push({
    json: {
      ...item.json,
      emailSubject: subject,
      emailBody: htmlBody,
      emailBodyPlain: body.replace(/<[^>]*>/g, '')
    }
  });
}

return results;

const allItems = $input.all();
const summary = {
  totalReminders: allItems.length,
  byUrgency: {},
  byType: {},
  totalAmountOutstanding: 0,
  clients: []
};

for (const item of allItems) {
  // Count by urgency
  summary.byUrgency[item.json.urgencyLevel] = (summary.byUrgency[item.json.urgencyLevel] || 0) + 1;

  // Count by type
  summary.byType[item.json.reminderType] = (summary.byType[item.json.reminderType] || 0) + 1;

  // Sum outstanding amounts
  summary.totalAmountOutstanding += parseFloat(item.json.invoice_amount);

  // Collect client info
  summary.clients.push({
    name: item.json.client_name,
    invoice: item.json.invoice_number,
    amount: item.json.invoice_amount,
    daysOverdue: item.json.daysOverdue,
    urgency: item.json.urgencyLevel
  });
}

return [{ json: summary }];
