// Data Validation Code for n8n
// This code cleans and validates testimonial data from the webhook

const items = $input.all();
const cleanedItems = [];

for (const item of items) {
  // Access the body data from webhook
  const data = item.json.body || item.json;

  // Extract and clean fields
  let name = (data.name || data.Name || '').trim();
  let designation = (data.designation || data.Designation || data.role || '').trim();
  let testimonial = (data.testimonial_text || data.testimonial || data.Testimonial || '').trim();
  let photoUrl = (data.photo_url || data.Photo || data.photo || '').trim();
  let email = (data.email || data.Email || '').trim();

  // Validation: Check required fields
  if (!name || name.length < 2) {
    name = 'Anonymous User';
  }

  if (!testimonial || testimonial.length < 10) {
    // Skip invalid testimonials
    console.log('Skipping invalid testimonial - too short or empty');
    continue;
  }

  // Set defaults for optional fields
  if (!designation) {
    designation = 'Valued Customer';
  }

  // Generate fallback avatar if no photo provided
  if (!photoUrl || !photoUrl.startsWith('http')) {
    photoUrl = 'https://ui-avatars.com/api/?name=' + encodeURIComponent(name) + '&size=400&background=4F46E5&color=fff&bold=true';
  }

  // Clean testimonial text - remove extra spaces and HTML tags
  testimonial = testimonial
    .replace(/\s+/g, ' ')    // Replace multiple spaces with single space
    .replace(/[<>]/g, '')     // Remove < and > to prevent XSS
    .trim();

  // Create clean object with all data
  cleanedItems.push({
    json: {
      name: name,
      designation: designation,
      testimonial: testimonial,
      photoUrl: photoUrl,
      email: email,
      timestamp: new Date().toISOString(),
      originalLength: testimonial.length,
      source: 'webhook'
    }
  });
}

// If no valid testimonials found, throw error
if (cleanedItems.length === 0) {
  throw new Error('No valid testimonials found after validation');
}

return cleanedItems;

// Extract AI Response Code (Updated for OpenAI format)
// This extracts the enhanced testimonial from OpenAI and merges with previous data

const items = $input.all();

return items.map((item) => {
  // Get the original data from Data Validation node
  const previousData = $('Data Validation').item.json;

  // Extract AI response from OpenAI's response format
  // OpenAI returns: [{ message: { content: "text" } }]
  let aiResponse;

  if (Array.isArray(item.json) && item.json[0]?.message?.content) {
    // Handle array format from OpenAI
    aiResponse = item.json[0].message.content;
  } else if (item.json.message?.content) {
    // Handle object format
    aiResponse = item.json.message.content;
  } else if (item.json.choices?.[0]?.message?.content) {
    // Handle choices format
    aiResponse = item.json.choices[0].message.content;
  } else if (item.json.text) {
    // Handle simple text format
    aiResponse = item.json.text;
  } else {
    // Fallback to original testimonial if extraction fails
    aiResponse = previousData.testimonial;
  }

  // Return merged data with both original and enhanced testimonial
  return {
    json: {
      ...previousData,                              // Keep all original data
      originalTestimonial: previousData.testimonial,  // Save original
      testimonial: aiResponse.trim(),                 // Use enhanced version
      enhanced: true,                                 // Mark as enhanced
      aiModel: 'gpt-4-turbo'                         // Track which model used
    }
  };
});

// Generate HTML Template Code - FIXED VERSION
// Creates a beautiful branded testimonial card with proper image handling

const items = $input.all();

