// Prepare Invalid Email Data - Code Node
// This handles the Verifi Email API response format

const items = $input.all();

return items.map(item => {
  // Extract the first item from the array (Verifi returns an array)
  const emailData = Array.isArray(item.json) ? item.json[0] : item.json;

  // Determine the specific reason for invalidity
  let invalidReason = '';
  let technicalReason = '';

  if (!emailData.details.validMxRecord) {
    invalidReason = 'Invalid or non-existent email domain';
    technicalReason = 'No valid MX records found for the domain';
  } else if (!emailData.details.rfcCompliant) {
    invalidReason = 'Email format is not compliant with standards';
    technicalReason = 'RFC non-compliant email format';
  } else if (emailData.details.disposable) {
    invalidReason = 'Disposable/temporary email address detected';
    technicalReason = 'Disposable email service';
  } else if (!emailData.details.spoofFree) {
    invalidReason = 'Potential spoofed or suspicious email';
    technicalReason = 'Email may be spoofed';
  } else {
    invalidReason = 'Email verification failed';
    technicalReason = 'Unknown verification issue';
  }

  // Get original subscriber data (name, email, source)
  // This assumes the original data is still in the item
  const originalData = item.json.name ? item.json : 
                       (item.json[0] && item.json[0].name ? item.json[0] : {});

  return {
    json: {
      // Original subscriber information
      name: originalData.name || 'Subscriber',
      email: emailData.email,
      source: originalData.source || 'Unknown',

      // Verification results
      valid: false,
      validMxRecord: emailData.details.validMxRecord,
      rfcCompliant: emailData.details.rfcCompliant,
      disposable: emailData.details.disposable,
      spoofFree: emailData.details.spoofFree,

      // Domain information
      domain: emailData.email.split('@')[1] || 'N/A',
      mxProvider: emailData.details.mx.provider || 'N/A',
      mxRecords: emailData.details.mx.records.join(', ') || 'None',

      // Reason messages
      invalidReason: invalidReason,
      technicalReason: technicalReason,
      // Timestamp
      timestamp: new Date().toISOString()
    }
  };
});

return $input.all().map(item => {
  // Check if this is from the valid path or invalid path
  const isValid = item.json.valid === true;

  return {
    json: {
      timestamp: item.json.timestamp || new Date().toISOString(),
      name: item.json.name,
      email: item.json.email,
      verification_result: isValid ? 'Valid' : 'Invalid',
      verification_score: 'N/A', // Verifi API doesn't provide score
      email_sent: isValid ? 'Yes' : 'No',
      source: item.json.source || 'Unknown',
      disposable: item.json.disposable || false,
      domain: item.json.domain || 'N/A',
      valid_mx_record: item.json.validMxRecord !== undefined ? item.json.validMxRecord : 'N/A',
      rfc_compliant: item.json.rfcCompliant !== undefined ? item.json.rfcCompliant : 'N/A',
      invalid_reason: item.json.invalidReason || 'N/A'
    }
  };
});
