const buyingCustomers = [];

for (const item of $input.all()) {
  const spent = parseFloat(item.json.total_spent || '0');

  if (item.json.is_paying_customer === true || spent > 0) {
    buyingCustomers.push(item);
  }
}

return buyingCustomers;

const products = $input.all();

const output = [];

for (const item of products) {
  const categories = item.json.categories || [];

  output.push({
    json: {
      product_id: item.json.id,
      product_name: item.json.name,
      categories
    }
  });
}

return output;

const items = $input.all();

// First, clean the "phone" field in billing of all orders
const cleanedItems = items.map(item => {
  const billing = item?.json?.billing;
  if (billing && billing.phone) {
    const rawPhone = String(billing.phone);
    const cleanedPhone = rawPhone.replace(/\D/g, ""); // remove non-digits
    item.json.billing.phone = cleanedPhone;
  }
  return item;
});

let topProduct = null;

// Then, find the top product by quantity from cleaned items
for (const order of cleanedItems) {
  const lineItems = order.json.line_items || [];
  const billing = order.json.billing || {};

  for (const item of lineItems) {
    if (item.product_id) {
      const currentProduct = {
        json: {
          product_id: item.product_id,
          product_name: item.name || "",
          quantity: item.quantity || 0,
          customer_name: `${billing.first_name || ''} ${billing.last_name || ''}`.trim(),
          email: billing.email || '',
          address: billing.address_1 || '',
          phone: billing.phone || ''
        }
      };

      if (!topProduct || currentProduct.json.quantity > topProduct.json.quantity) {
        topProduct = currentProduct;
      }
    }
  }
}

return topProduct ? [topProduct] : [];
