// Get the next week date range from Workflow Configuration
const nextWeekStart = new Date($('Workflow Configuration').first().json.nextWeekStart);
const nextWeekEnd = new Date($('Workflow Configuration').first().json.nextWeekEnd);

// Combine all holiday data from the loop
const allHolidays = [];

// Process all input items (which are the results from each loop iteration)
for (const item of $input.all()) {
  // The HTTP Request node in the loop outputs the holiday array in item.json
  if (Array.isArray(item.json)) {
    for (const holiday of item.json) {
      allHolidays.push(holiday);
    }
  }
}

// Filter holidays that fall within next week range
const nextWeekHolidays = allHolidays.filter(holiday => {
  const holidayDate = new Date(holiday.date);
  return holidayDate >= nextWeekStart && holidayDate <= nextWeekEnd;
});

// Map to desired output format
const formattedHolidays = nextWeekHolidays.map(holiday => ({
  date: holiday.date,
  name: holiday.name || holiday.localName,
  country: holiday.countryCode === 'US' ? 'United States' : 
           holiday.countryCode === 'GB' ? 'United Kingdom' : 
           holiday.countryCode === 'DE' ? 'Germany' : 
           holiday.countryCode === 'IN' ? 'India' : 
           holiday.countryCode === 'CN' ? 'China' : 
           holiday.countryCode === 'KR' ? 'South Korea' : 
           holiday.countryCode === 'HK' ? 'Hong Kong' : holiday.countryCode,
  countryCode: holiday.countryCode
}));

// Return all filtered holidays as a single item
return [{
  json: {
    holidays: formattedHolidays
  }
}];

// Get the single item containing the list of all holidays from the first input
const holidayList = $input.all(0)[0].json.holidays || [];

// Get all items containing calendar events from the second input
const calendarEventItems = $input.all(1);

// Create a map of holiday dates for quick lookup
const holidayMap = new Map();
holidayList.forEach(holiday => {
  const date = holiday.date;
  if (!holidayMap.has(date)) {
    holidayMap.set(date, []);
  }
  holidayMap.get(date).push({
    name: holiday.name,
    country: holiday.countryCode || holiday.country
  });
});

// Detect conflicts by iterating through each calendar event item
const conflicts = [];
calendarEventItems.forEach(eventItem => {
  const event = eventItem.json;
  const eventStart = event.start?.dateTime || event.start?.date;
  if (!eventStart) return; // Skip if no start time

  const eventDate = eventStart.split('T')[0];

  // Check if this date has any holidays
  if (holidayMap.has(eventDate)) {
    const holidaysOnDate = holidayMap.get(eventDate);
    const eventTime = eventStart.includes('T') ? eventStart.split('T')[1].substring(0, 5) : 'All day';
    const attendees = event.attendees ? event.attendees.map(a => a.email) : [];

    conflicts.push({
      eventName: event.summary || 'Untitled Event',
      eventDate: eventDate,
      eventTime: eventTime,
      holidayName: holidaysOnDate.map(h => h.name).join(', '),
      affectedCountries: [...new Set(holidaysOnDate.map(h => h.country))].join(', '),
      attendees: attendees,
      eventId: event.id
    });
  }
});

// Return all found conflicts in a SINGLE item to ensure subsequent nodes run only once
return [{
  json: {
    conflicts: conflicts,
    totalConflicts: conflicts.length,
    checkDate: new Date().toISOString()
  }
}];

const conflicts = $input.first().json.conflicts || [];

// Get the holiday list from the 'Merge and Filter' node which ran before the conflict detection
const holidays = $('Merge and Filter Next Week Holidays').first().json.holidays.map(h => h.date);

function isHoliday(dateStr) {
  return holidays.includes(dateStr);
}

function isWeekend(date) {
  const day = date.getDay();
  return day === 0 || day === 6; // Sunday or Saturday
}

function findNextAvailableDate(startDate) {
  let currentDate = new Date(startDate);
  currentDate.setDate(currentDate.getDate() + 1);

  for (let i = 0; i < 30; i++) {
    const dateStr = currentDate.toISOString().split('T')[0];
    if (!isWeekend(currentDate) && !isHoliday(dateStr)) {
      const originalTime = startDate.toTimeString().split(' ')[0].substring(0, 5);
      return { date: dateStr, time: originalTime };
    }
    currentDate.setDate(currentDate.getDate() + 1);
  }

  return {
    date: currentDate.toISOString().split('T')[0],
    time: startDate.toTimeString().split(' ')[0].substring(0, 5)
  };
}

const enhancedConflicts = conflicts.map(conflict => {
  const eventDateTime = conflict.eventTime === 'All day' ? new Date(conflict.eventDate) : new Date(`${conflict.eventDate}T${conflict.eventTime}`);
  const suggestion = findNextAvailableDate(eventDateTime);

  return {
    ...conflict,
    suggestedDate: suggestion.date,
    suggestedTime: suggestion.time,
    originalDate: conflict.eventDate,
    originalTime: conflict.eventTime
  };
});

return [{
  json: {
    conflicts: enhancedConflicts,
    totalConflicts: enhancedConflicts.length
  }
}];

// Get the conflicts data from the previous node
const conflictsData = $input.first().json;
const conflicts = conflictsData.conflicts || [];

// Use a single backslash \n for newlines
let slackMessage = ':warning: *Holiday Conflict Alert* :warning:\n\n';

if (conflicts.length === 0) {
  slackMessage += 'No conflicts detected for next week. All clear! :white_check_mark:';
} else {
  slackMessage += `Found *${conflicts.length}* meeting(s) scheduled during public holidays next week:\n\n`;

  conflicts.forEach((conflict, index) => {
    slackMessage += `*${index + 1}. ${conflict.eventName}*\n`;
    slackMessage += `:calendar: *Date:* ${conflict.originalDate}\n`;
    slackMessage += `:clock3: *Time:* ${conflict.originalTime}\n`;
    slackMessage += `:earth_americas: *Affected Countries:* ${conflict.affectedCountries}\n`;
    slackMessage += `:pushpin: *Holiday:* ${conflict.holidayName}\n`;

    // Add suggestion if it exists
    if (conflict.suggestedDate) {
      slackMessage += `:bulb: *Suggestion:* Reschedule to ${conflict.suggestedDate} at ${conflict.suggestedTime}\n`;
    }

    slackMessage += '\n---\n\n';
  });

  slackMessage += ':point_right: Please review and reschedule these meetings to accommodate team members in affected regions.';
}

// Return the formatted message
return [{
  json: {
    slackMessage: slackMessage
  }
}];
