// AI-powered competitive offer analysis
const items = $input.all();
const analyzedItems = [];

for (const item of items) {
  const data = item.json;
  let competitiveScore = 0;
  let insights = [];
  let recommendations = [];
  let alertLevel = 'normal';

  // Extract competitor data
  const competitorName = data.competitorName || 'Unknown';
  const commissionRate = parseFloat(data.commissionRate) || 0;
  const cookieDuration = parseInt(data.cookieDuration) || 0;
  const averageOrderValue = parseFloat(data.averageOrderValue) || 0;
  const conversionRate = parseFloat(data.conversionRate) || 0;
  const payoutThreshold = parseFloat(data.payoutThreshold) || 0;

  // Get your current rates from workflow context
  const yourCommissionRate = 10; // Your current rate
  const yourCookieDuration = 30; // Your cookie days
  const yourAOV = 150; // Your AOV

  // Commission Rate Analysis
  const commissionDiff = commissionRate - yourCommissionRate;
  if (commissionDiff > 5) {
    competitiveScore += 35;
    insights.push(`HIGH ALERT: ${competitorName} offers ${commissionRate}% commission vs your ${yourCommissionRate}% (+${commissionDiff.toFixed(1)}% advantage)`);
    recommendations.push(`URGENT: Consider matching or beating ${competitorName}'s ${commissionRate}% commission rate`);
    alertLevel = 'critical';
  } else if (commissionDiff > 2) {
    competitiveScore += 25;
    insights.push(`WARNING: ${competitorName} offers ${commissionRate}% commission vs your ${yourCommissionRate}% (+${commissionDiff.toFixed(1)}% advantage)`);
    recommendations.push(`Review commission structure to stay competitive with ${competitorName}`);
    alertLevel = alertLevel === 'critical' ? 'critical' : 'warning';
  } else if (commissionDiff > 0) {
    competitiveScore += 15;
    insights.push(`${competitorName} offers slightly higher commission: ${commissionRate}% vs ${yourCommissionRate}%`);
  } else if (commissionDiff < -3) {
    insights.push(`YOUR ADVANTAGE: You offer ${yourCommissionRate}% vs ${competitorName}'s ${commissionRate}% (-${Math.abs(commissionDiff).toFixed(1)}% in your favor)`);
    recommendations.push(`Promote your superior ${yourCommissionRate}% commission rate in affiliate recruitment`);
  } else {
    insights.push(`Commission rates are competitive: ${commissionRate}% vs your ${yourCommissionRate}%`);
  }

  // Cookie Duration Analysis
  const cookieDiff = cookieDuration - yourCookieDuration;
  if (cookieDiff > 30) {
    competitiveScore += 25;
    insights.push(`HIGH ALERT: ${competitorName} offers ${cookieDuration}-day cookies vs your ${yourCookieDuration} days`);
    recommendations.push(`URGENT: Extend cookie duration to match ${competitorName}'s ${cookieDuration} days`);
    alertLevel = 'critical';
  } else if (cookieDiff > 15) {
    competitiveScore += 15;
    insights.push(`WARNING: ${competitorName} offers longer cookies: ${cookieDuration} vs ${yourCookieDuration} days`);
    recommendations.push(`Consider extending cookie duration to improve conversion attribution`);
    alertLevel = alertLevel === 'critical' ? 'critical' : 'warning';
  } else if (cookieDiff > 0) {
    competitiveScore += 8;
    insights.push(`${competitorName} has slightly longer cookies: ${cookieDuration} vs ${yourCookieDuration} days`);
  } else if (cookieDiff < -15) {
    insights.push(`YOUR ADVANTAGE: ${yourCookieDuration}-day cookies vs ${competitorName}'s ${cookieDuration} days`);
    recommendations.push(`Highlight your superior ${yourCookieDuration}-day cookie window in marketing`);
  }

  // Average Order Value Analysis
  const aovDiff = averageOrderValue - yourAOV;
  if (averageOrderValue > 0) {
    if (aovDiff > 100) {
      competitiveScore += 20;
      insights.push(`${competitorName} has higher AOV: $${averageOrderValue} vs your $${yourAOV}`);
      recommendations.push(`Target higher-value products or bundles to compete with ${competitorName}`);
    } else if (aovDiff > 50) {
      competitiveScore += 10;
      insights.push(`${competitorName} AOV slightly higher: $${averageOrderValue} vs $${yourAOV}`);
    } else if (aovDiff < -50) {
      insights.push(`YOUR ADVANTAGE: Higher AOV of $${yourAOV} vs ${competitorName}'s $${averageOrderValue}`);
      recommendations.push(`Emphasize your higher earnings per conversion: $${yourAOV}`);
    }
  }

  // Conversion Rate Analysis
  if (conversionRate > 0) {
    if (conversionRate > 5) {
      competitiveScore += 15;
      insights.push(`${competitorName} reports strong ${conversionRate}% conversion rate`);
      recommendations.push(`Research ${competitorName}'s landing page optimization tactics`);
    } else if (conversionRate > 3) {
      competitiveScore += 8;
      insights.push(`${competitorName} has good ${conversionRate}% conversion rate`);
    }
  }

  // Payout Analysis
  if (payoutThreshold > 0) {
    if (payoutThreshold < 50) {
      competitiveScore += 10;
      insights.push(`${competitorName} has low $${payoutThreshold} payout threshold (affiliate-friendly)`);
      recommendations.push(`Consider lowering your payout threshold to improve cash flow for affiliates`);
    } else if (payoutThreshold > 100) {
      insights.push(`YOUR ADVANTAGE: Lower payout threshold than ${competitorName}'s $${payoutThreshold}`);
    }
  }

  // Calculate EPC (Earnings Per Click)
  const theirEPC = (averageOrderValue * commissionRate / 100 * conversionRate / 100).toFixed(2);
  const yourEPC = (yourAOV * yourCommissionRate / 100 * 3 / 100).toFixed(2); // Assuming 3% conversion

  if (parseFloat(theirEPC) > parseFloat(yourEPC) * 1.3) {
    insights.push(`ALERT: ${competitorName} estimated EPC $${theirEPC} vs your $${yourEPC}`);
    recommendations.push(`Focus on improving your EPC through better commission or conversion optimization`);
  } else if (parseFloat(yourEPC) > parseFloat(theirEPC) * 1.2) {
    insights.push(`YOUR ADVANTAGE: Superior EPC of $${yourEPC} vs ${competitorName}'s $${theirEPC}`);
    recommendations.push(`Promote your higher earnings potential: $${yourEPC} EPC`);
  }

  // Determine threat level
  let threatLevel = 'Low';
  if (competitiveScore >= 70) {
    threatLevel = 'Critical';
  } else if (competitiveScore >= 45) {
    threatLevel = 'High';
  } else if (competitiveScore >= 25) {
    threatLevel = 'Medium';
  }

  // Strategic recommendations based on threat
  if (threatLevel === 'Critical' || threatLevel === 'High') {
    recommendations.push('Schedule emergency meeting with affiliate team');
    recommendations.push('Analyze affiliate churn risk immediately');
    recommendations.push('Prepare competitive response strategy within 48 hours');
  } else if (threatLevel === 'Medium') {
    recommendations.push('Monitor closely for additional changes');
    recommendations.push('Survey top affiliates about competitive pressures');
  } else {
    recommendations.push('Continue monitoring - maintain current competitive position');
    recommendations.push('Look for opportunities to widen competitive advantage');
  }

  analyzedItems.push({
    json: {
      ...data,
      competitorName: competitorName,
      competitiveScore: competitiveScore,
      threatLevel: threatLevel,
      alertLevel: alertLevel,
      insights: insights,
      recommendations: recommendations,
      comparison: {
        commission: {
          theirs: commissionRate,
          yours: yourCommissionRate,
          diff: commissionDiff.toFixed(1)
        },
        cookies: {
          theirs: cookieDuration,
          yours: yourCookieDuration,
          diff: cookieDiff
        },
        aov: {
          theirs: averageOrderValue,
          yours: yourAOV,
          diff: aovDiff.toFixed(2)
        },
        epc: {
          theirs: theirEPC,
          yours: yourEPC
        }
      },
      analyzedAt: new Date().toISOString()
    }
  });
}

return analyzedItems;

