// Loop over input items and add a new field called 'myNewField' to the JSON of each one

let tickets = JSON.parse($input.first().json.body.tickets) 
let tickets_text = []
for(var ticket of tickets) {
  if(ticket.properties) tickets_text.push(ticket.properties.subject.value + "\n" + ticket.properties.content.value)
  else tickets_text.push(ticket.subject + "\n" + ticket.content)

}

return {tickets_text}

