// Parse AI response and clean data
const items = [];

for (const item of $input.all()) {
  try {
    // Parse the AI response
    let parsed;
    const response = item.json.choices?.[0]?.message?.content || item.json.message || '';

    // Remove markdown code blocks if present
    const cleaned = response.replace(/```json\n?|```\n?/g, '').trim();

    try {
      parsed = JSON.parse(cleaned);
    } catch (e) {
      // Try to extract JSON from the response
      const jsonMatch = cleaned.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        parsed = JSON.parse(jsonMatch[0]);
      } else {
        throw new Error('Could not parse JSON from AI response');
      }
    }

    // Enrich with metadata
    items.push({
      json: {
        ...parsed,
        scrapedAt: new Date().toISOString(),
        priceChange: 0, // Will be calculated in comparison
        priceChangePercent: 0,
        isNewLow: false,
        alertLevel: 'none'
      }
    });
  } catch (error) {
    console.error('Failed to parse item:', error.message);
    // Add error item for debugging
    items.push({
      json: {
        error: error.message,
        rawResponse: item.json,
        competitorName: 'Parse Error',
        scrapedAt: new Date().toISOString()
      }
    });
  }
}

return items;

// Compare current prices with historical and detect changes
const results = [];

for (const item of $input.all()) {
  const current = item.json;

  // Skip error items
  if (current.error) {
    results.push({ json: current });
    continue;
  }

  // Find historical data for this competitor
  const historical = $('üìä Read Historical Data').all()
    .find(h => h.json.competitorName === current.competitorName);

  let alertLevel = 'none';
  let changes = [];

  if (historical && historical.json.currentPrice) {
    const oldPrice = parseFloat(historical.json.currentPrice);
    const newPrice = parseFloat(current.currentPrice);
    const priceChange = newPrice - oldPrice;
    const priceChangePercent = ((priceChange / oldPrice) * 100).toFixed(2);

    current.priceChange = priceChange;
    current.priceChangePercent = parseFloat(priceChangePercent);
    current.previousPrice = oldPrice;

    // Determine alert level based on price changes
    if (Math.abs(priceChangePercent) >= 20) {
      alertLevel = 'critical';
      changes.push(`Price ${priceChange > 0 ? 'increased' : 'decreased'} by ${Math.abs(priceChangePercent)}%`);
    } else if (Math.abs(priceChangePercent) >= 10) {
      alertLevel = 'warning';
      changes.push(`Price ${priceChange > 0 ? 'increased' : 'decreased'} by ${Math.abs(priceChangePercent)}%`);
    } else if (Math.abs(priceChangePercent) >= 5) {
      alertLevel = 'info';
      changes.push(`Minor price change: ${priceChangePercent}%`);
    }

    // Check if it's a new low price
    const historicalLow = parseFloat(historical.json.lowestPrice || oldPrice);
    if (newPrice < historicalLow) {
      current.isNewLow = true;
      alertLevel = alertLevel === 'none' ? 'info' : alertLevel;
      changes.push('üéØ NEW LOWEST PRICE!');
    }
    current.lowestPrice = Math.min(newPrice, historicalLow);

    // Stock level changes
    if (historical.json.stockLevel !== current.stockLevel) {
      if (current.stockLevel === 'Out of Stock') {
        alertLevel = 'critical';
        changes.push('üì¶ Product went OUT OF STOCK');
      } else if (current.stockLevel === 'Low Stock') {
        alertLevel = alertLevel === 'none' ? 'warning' : alertLevel;
        changes.push('‚ö†Ô∏è Stock level is LOW');
      } else if (historical.json.stockLevel === 'Out of Stock' && current.inStock) {
        alertLevel = alertLevel === 'none' ? 'info' : alertLevel;
        changes.push('‚úÖ Back in stock!');
      }
    }

    // Rating changes
    const oldRating = parseFloat(historical.json.rating || 0);
    const newRating = parseFloat(current.rating || 0);
    const ratingChange = newRating - oldRating;

    if (Math.abs(ratingChange) >= 0.5) {
      alertLevel = alertLevel === 'none' ? 'info' : alertLevel;
      changes.push(`‚≠ê Rating ${ratingChange > 0 ? 'improved' : 'dropped'} by ${Math.abs(ratingChange).toFixed(1)} stars`);
    }

    // Review count changes
    const oldReviews = parseInt(historical.json.reviewCount || 0);
    const newReviews = parseInt(current.reviewCount || 0);
    const reviewDiff = newReviews - oldReviews;

    if (reviewDiff > 0) {
      changes.push(`üí¨ ${reviewDiff} new review${reviewDiff > 1 ? 's' : ''}`);
    }
  } else {
    // First time seeing this competitor
    alertLevel = 'info';
    changes.push('üÜï First time tracking this competitor');
    current.lowestPrice = current.currentPrice;
  }

  current.alertLevel = alertLevel;
  current.changesSummary = changes.join(' | ');
  current.hasChanges = alertLevel !== 'none';

  results.push({ json: current });
}

return results;
