// Build article content
  const combinedContent = articles.map(article => {
    let content = `## ${article.title}\n`;
    if (article.original_url) content += `**Original URL:** ${article.original_url}\n`;
    if (article.author) content += `**Author:** ${article.author}\n`;
    if (article.content) content += `**Summary:** ${article.content}\n`;
    content += `**Highlights:** ${article.highlight_count}\n`;
    content += `**Reading Progress:** ${Math.round(article.reading_progress * 100)}%\n\n`;

    if (article.highlights && article.highlights.length > 0) {
      content += `**Key Highlights:**\n`;
      article.highlights.forEach(highlight => {
        content += `- "${highlight.text}"\n`;
        if (highlight.note) {
          content += `  *Note: ${highlight.note}*\n`;
        }
      });
    }

    content += `\n---\n\n`;
    return content;
  }).join('');

