const all = $input.all();
const brief = all.find(i => i.json.clientName)?.json || {};
const budgets = all.filter(i => i.json.budgetAmount !== undefined).map(i => i.json);
const actuals = all.filter(i => i.json.actualCost !== undefined).map(i => i.json);
const vendors = all.filter(i => i.json.vendorName).map(i => i.json);

// Metadata
const meta = {
  eventId: brief.eventId || `EVT-${Date.now()}`,
  clientName: brief.clientName || 'Client',
  eventType: brief.eventType || 'Conference',
  attendees: parseInt(brief.attendees) || 150,
  targetBudget: parseFloat(brief.budget) || 75000,
  eventDate: brief.eventDate || 'TBD',
  plannerEmail: brief.plannerEmail || 'user@example.com',
  spreadsheetId: brief.spreadsheetId || '1234567890abcdefghijklmnop',
  teamChannel: brief.teamChannel || '#event-orchestration',
  priority: brief.priority || 'Medium'
};

// Budget aggregation
let totalBudgeted = 0, totalEstimated = 0, totalActual = 0;
const categories = {};

budgets.forEach(b => {
  const cat = b.category || 'Other';
  const budgeted = parseFloat(b.budgetAmount || 0);
  const estimated = parseFloat(b.estimatedCost || 0);
  totalBudgeted += budgeted;
  totalEstimated += estimated;
  if (!categories[cat]) categories[cat] = { budgeted: 0, estimated: 0, actual: 0, items: [] };
  categories[cat].budgeted += budgeted;
  categories[cat].estimated += estimated;
  categories[cat].items.push(b);
});

actuals.forEach(a => {
  const cat = a.category || 'Other';
  const actual = parseFloat(a.actualCost || 0);
  totalActual += actual;
  if  if (categories[cat]) categories[cat].actual += actual;
});

const variance = totalBudgeted - totalEstimated;
const variancePct = totalBudgeted > 0 ? ((variance / totalBudgeted) * 100).toFixed(2) : 0;

return {
  json: {
    ...meta,
    budgets, actuals, vendors,
    totalBudgeted: totalBudgeted.toFixed(2),
    totalEstimated: totalEstimated.toFixed(2),
    totalActual: totalActual.toFixed(2),
    variance: variance.toFixed(2),
    variancePct,
    overBudget: variance < 0,
    categories: Object.values(categories),
    validation: { errors: [], warnings: [] },
    timestamp: new Date().toISOString()
  }
};

