const ai = $input.first().json.response || $input.first().json.text;
const base = $('Data Fusion Engine').first().json;
let plan; try { plan = JSON.parse(ai.match(/\{[\s\S]*\}/)[0]); } catch { plan = { planSummary: 'AI parse failed' }; }

const savings = (plan.optimizedBudgetBreakdown || []).reduce((s, c) => s + (c.savings || 0), 0);
const final = base.totalEstimated - savings;
const roi = ((savings / base.totalEstimated) * 100).toFixed(1);
const riskScore = (plan.riskAnalysis || []).reduce((s, r) => s + (r.probability === 'High' ? 3 : r.probability === 'Medium' ? 2 : 1) * (r.impact === 'High' ? 3 : 2 : 1), 0);
const riskLevel = riskScore > 12 ? 'High' : riskScore > 6 ? 'Medium' : 'Low';
const autoApprove = plan.autoApproval !== false && riskLevel !== 'High' && savings > 0;

return { json: { ...base, ...plan, totalSavings: savings.toFixed(2), finalCost: final.toFixed(2), roiImprovement: roi, riskScore, riskLevel, autoApprove, orchestratedAt: new Date().toISOString() } };

