// Validate and parse incoming request
const payload = $input.first().json.body || $input.first().json;

const requestData = {
  eventType: payload.eventType || "Conference",
  venueCapacity: parseInt(payload.venueCapacity) || 500,
  attendeeCount: parseInt(payload.attendeeCount) || 350,
  layoutPreference: payload.layoutPreference || "Theater",
  venueDimensions: payload.venueDimensions || { width: 30, length: 40 }, // in meters
  specialRequirements: payload.specialRequirements || [],
  accessibilityNeeds: parseInt(payload.accessibilityNeeds) || 10,
  vipCount: parseInt(payload.vipCount) || 0,
  requestId: payload.requestId || `seat-${Date.now()}`,
  timestamp: new Date().toISOString()
};

// Validate capacity
if (requestData.attendeeCount > requestData.venueCapacity) {
  return {
    json: {
      error: true,
      message: "Attendee count exceeds venue capacity",
      requestData
    }
  };
}

return { json: requestData };

// Combine all data for processing
const requestData = $input.all()[0].json;
const attendees = $input.all()[1].json;
const venueTemplates = $input.all()[2].json;

// Structure combined data
const combinedData = {
  request: requestData,
  attendees: Array.isArray(attendees) ? attendees : [attendees],
  venueTemplates: Array.isArray(venueTemplates) ? venueTemplates : [venueTemplates],
  processingTimestamp: new Date().toISOString()
};

return { json: combinedData };

const data = $input.first().json;
const request = data.request;
const attendees = data.attendees;
const templates = data.venueTemplates;

// Select appropriate layout template
function selectLayoutTemplate(eventType, layoutPref, templates) {
  const template = templates.find(t => 
    t.eventType === eventType && t.layoutType === layoutPref
  ) || templates[0] || {
    layoutType: layoutPref,
    seatsPerRow: 20,
    rowSpacing: 1.2,
    aisleWidth: 1.5,
    stageDepth: 5
  };

  return template;
}

// Calculate optimal layout dimensions
function calculateLayout(attendeeCount, venueSize, template) {
  const seatsPerRow = template.seatsPerRow || 20;
  const totalRows = Math.ceil(attendeeCount / seatsPerRow);
  const rowSpacing = template.rowSpacing || 1.2;
  const aisleWidth = template.aisleWidth || 1.5;

  // Calculate number of aisles needed
  const aislesNeeded = Math.floor(seatsPerRow / 10);

  // Calculate actual width needed
  const seatWidth = 0.6; // meters
  const widthNeeded = (seatsPerRow * seatWidth) + (aislesNeeded * aisleWidth);
  const lengthNeeded = (totalRows * rowSpacing) + (template.stageDepth || 5);

  return {
    totalRows,
    seatsPerRow,
    aislesNeeded,
    dimensions: {
      width: widthNeeded,
      length: lengthNeeded
    },
    feasible: widthNeeded <= venueSize.width && lengthNeeded <= venueSize.length
  };
}

// Group attendees by type
function categorizeAttendees(attendees) {
  const vips = attendees.filter(a => a.vipStatus === true || a.type === 'VIP');
  const accessibility = attendees.filter(a => a.accessibility === true || a.accessibilityNeeds);
  const groups = {};

  attendees.forEach(a => {
    const group = a.group || a.company || 'General';
    if (!groups[group]) groups[group] = [];
    groups[group].push(a);
  });

  return { vips, accessibility, groups };
}

// Generate seating assignments
function generateSeatingPlan(layout, categorized, totalAttendees) {
  const seatingPlan = [];
  let currentRow = 1;
  let currentSeat = 1;

  // Assign VIPs to front rows
  categorized.vips.forEach((vip, index) => {
    seatingPlan.push({
      attendeeId: vip.id || vip.name,
      attendeeName: vip.name,
      section: 'VIP',
      row: Math.floor(index / layout.seatsPerRow) + 1,
      seat: (index % layout.seatsPerRow) + 1,
      type: 'VIP',
      notes: vip.notes || ''
    });
  });

  // Reserve accessible seating near aisles
  let accessibleSeatsAssigned = 0;
  categorized.accessibility.forEach((person, index) => {
    const rowNum = Math.floor(layout.totalRows / 2) + Math.floor(index / 2);
    seatingPlan.push({
      attendeeId: person.id || person.name,
      attendeeName: person.name,
      section: 'Accessible',
      row: rowNum,
      seat: index % 2 === 0 ? 1 : layout.seatsPerRow, // Aisle seats
      type: 'Accessible',
      notes: person.accessibilityNeeds || 'Wheelchair accessible'
    });
    accessibleSeatsAssigned++;
  });

  // Assign groups together
  let generalSeatCounter = categorized.vips.length;
  Object.keys(categorized.groups).forEach(groupName => {
    const groupMembers = categorized.groups[groupName];

    groupMembers.forEach((member, index) => {
      // Skip if already assigned (VIP or accessible)
      if (seatingPlan.find(s => s.attendeeId === (member.id || member.name))) {
        return;
      }

      const rowNum = Math.floor(generalSeatCounter / layout.seatsPerRow) + 1;
      const seatNum = (generalSeatCounter % layout.seatsPerRow) + 1;

      seatingPlan.push({
        attendeeId: member.id || member.name,
        attendeeName: member.name,
        section: 'General',
        row: rowNum,
        seat: seatNum,
        type: 'Group',
        group: groupName,
        notes: member.notes || ''
      });

      generalSeatCounter++;
    });
  });

  return seatingPlan;
}

// Main processing
const template = selectLayoutTemplate(
  request.eventType,
  request.layoutPreference,
  templates
);

const layout = calculateLayout(
  request.attendeeCount,
  request.venueDimensions,
  template
);

const categorized = categorizeAttendees(attendees);
const seatingPlan = generateSeatingPlan(layout, categorized, request.attendeeCount);

// Calculate statistics
const stats = {
  totalSeats: layout.totalRows * layout.seatsPerRow,
  assignedSeats: seatingPlan.length,
  availableSeats: (layout.totalRows * layout.seatsPerRow) - seatingPlan.length,
  utilizationRate: ((seatingPlan.length / (layout.totalRows * layout.seatsPerRow)) * 100).toFixed(2),
  vipSeats: categorized.vips.length,
  accessibleSeats: categorized.accessibility.length,
  groupCount: Object.keys(categorized.groups).length
};

// Generate output
const result = {
  requestId: request.requestId,
  timestamp: new Date().toISOString(),
  eventType: request.eventType,
  layoutType: request.layoutPreference,
  venue: {
    capacity: request.venueCapacity,
    dimensions: request.venueDimensions,
    feasible: layout.feasible
  },
  layout: {
    totalRows: layout.totalRows,
    seatsPerRow: layout.seatsPerRow,
    aisles: layout.aislesNeeded,
    calculatedDimensions: layout.dimensions
  },
  seatingPlan: seatingPlan,
  statistics: stats,
  visualMap: generateVisualMap(layout, seatingPlan)
};

// Generate ASCII visual map
function generateVisualMap(layout, seating) {
  const map = [];
  map.push('\n=== VENUE LAYOUT MAP ===\n');
  map.push('[STAGE]'.padStart(layout.seatsPerRow * 2, ' '));
  map.push('\n');

  for (let row = 1; row <= layout.totalRows; row++) {
    let rowStr = `R${row.toString().padStart(2, '0')} `;

    for (let seat = 1; seat <= layout.seatsPerRow; seat++) {
      const assignment = seating.find(s => s.row === row && s.seat === seat);

      if (assignment) {
        if (assignment.type === 'VIP') rowStr += 'V ';
        else if (assignment.type === 'Accessible') rowStr += 'A ';
        else rowStr += 'X ';
      } else {
        rowStr += '‚óã ';
      }

      // Add aisle
      if (seat % 10 === 0 && seat !== layout.seatsPerRow) {
        rowStr += '| ';
      }
    }

    map.push(rowStr);
  }

  map.push('\n\nLegend: V=VIP | A=Accessible | X=Assigned | ‚óã=Available\n');

  return map.join('\n');
}

return { json: result };

const layoutData = $input.first().json;

// Format recommendations in natural language
const recommendations = [];

if (!layoutData.venue.feasible) {
  recommendations.push('‚ö†Ô∏è WARNING: Current layout exceeds venue dimensions. Consider reducing seats per row or attendee count.');
}

if (layoutData.statistics.utilizationRate > 95) {
  recommendations.push('‚ö†Ô∏è High capacity utilization. Consider adding buffer space for comfort.');
}

if (layoutData.statistics.utilizationRate < 70) {
  recommendations.push('‚úì Good space utilization with room for comfort and movement.');
}

if (layoutData.layout.aisles < 2) {
  recommendations.push('üí° Consider adding more aisles for better traffic flow.');
}

if (layoutData.statistics.accessibleSeats > 0) {
  recommendations.push(`‚úì ${layoutData.statistics.accessibleSeats} accessible seats reserved near aisles.`);
}

if (layoutData.statistics.vipSeats > 0) {
  recommendations.push(`‚úì ${layoutData.statistics.vipSeats} VIP seats assigned in front rows.`);
}

recommendations.push(`‚úì ${layoutData.statistics.groupCount} groups seated together for better networking.`);

// Create formatted output
const formattedOutput = {
  ...layoutData,
  recommendations: recommendations,
  summary: `Successfully generated seating plan for ${layoutData.statistics.assignedSeats} attendees in ${layoutData.layout.totalRows} rows with ${layoutData.statistics.availableSeats} buffer seats.`,
  exportReady: true
};

return { json: formattedOutput };

const layoutData = $input.first().json;

// Split seating plan into individual seat records
const seatAssignments = layoutData.seatingPlan.map(seat => ({
  requestId: layoutData.requestId,
  timestamp: layoutData.timestamp,
  attendeeId: seat.attendeeId,
  attendeeName: seat.attendeeName,
  section: seat.section,
  row: seat.row,
  seat: seat.seat,
  type: seat.type,
  group: seat.group || '',
  notes: seat.notes || ''
}));

return seatAssignments.map(seat => ({ json: seat }));
