// Get input data (assuming one item only)
const order = items[0].json;

// Extract important data
const result = {
  number: order.number,
  orderNumber: order.order_number,
  orderUrl: order.order_status_url,
  confirmationNumber: order.confirmation_number,
  createdAt: order.created_at,
  cancelledAt: order.cancelled_at,
  status: {
    financial: order.financial_status,
    fulfillment: order.fulfillment_status,
  },
  total: {
    price: order.total_price,
    subtotal: order.subtotal_price,
    tax: order.total_tax,
    currency: order.currency,
  },
  customer: {
    email: order.customer?.email || null,
    phone: order.customer?.phone || null,
    name: `${order.customer?.first_name || ''} ${order.customer?.last_name || ''}`.trim(),
  },
  address: {
    billing: {
      name: `${order.billing_address?.first_name || ''} ${order.billing_address?.last_name || ''}`.trim(),
      phone: order.billing_address?.phone || null,
      address1: order.billing_address?.address1 || null,
      address2: order.billing_address?.address2 || null,
      city: order.billing_address?.city || null,
      zip: order.billing_address?.zip || null,
      country: order.billing_address?.country || null,
    },
    shipping: {
      name: `${order.shipping_address?.first_name || ''} ${order.shipping_address?.last_name || ''}`.trim(),
      phone: order.shipping_address?.phone || null,
      address1: order.shipping_address?.address1 || null,
      address2: order.shipping_address?.address2 || null,
      city: order.shipping_address?.city || null,
      zip: order.shipping_address?.zip || null,
      country: order.shipping_address?.country || null,
    }
  },
  product: order.line_items.map(item => ({
    title: item.title,
    price: item.price,
    quantity: item.quantity,
    tax: item.tax_lines?.[0]?.price || 0,
  })),
  fulfillment: order.fulfillments?.[0]
    ? {
        trackingNumber: order.fulfillments[0].tracking_number,
        trackingUrl: order.fulfillments[0].tracking_url,
        status: order.fulfillments[0].status,
      }
    : null,
  refund: order.refunds?.[0]
    ? {
        reason: order.refunds[0].note || 'N/A',
        amount: order.refunds[0].transactions?.[0]?.amount || '0',
        date: order.refunds[0].created_at,
      }
    : null,
};

// Return simplified data
return [{ json: result }];

const items = await $input.all();

const updatedItems = items.map((item) => {
  let rawNumber = item?.json?.address?.billing?.phone;
  rawNumber = rawNumber ? String(rawNumber) : "";

  const cleanedNumber = rawNumber.replace(/\D/g, ""); // Remove non-digit characters

  // Update the nested phone field
  if (item.json.address?.billing) {
    item.json.address.billing.phone = cleanedNumber;
  }

  return item;
});

return updatedItems;
