// Parse the AI response to extract competitor data
const response = $input.first().json.output;

// Try to extract JSON from the response
let competitors;
try {
  // Try to parse directly
  competitors = JSON.parse(response);
} catch (e) {
  // Try to extract JSON from markdown code blocks
  const jsonMatch = response.match(/```json\n([\s\S]*?)\n```/);
  if (jsonMatch) {
    competitors = JSON.parse(jsonMatch[1]);
  } else {
    // Try to find JSON object in the text
    const objMatch = response.match(/\{[\s\S]*\}/);
    if (objMatch) {
      competitors = JSON.parse(objMatch[0]);
    } else {
      throw new Error('Could not extract JSON from response');
    }
  }
}

// Extract competitor URLs and names
const competitorData = competitors.competitors.map(comp => ({
  competitor_name: comp.company_name,
  competitor_url: comp.website_url,
  overlap_percentage: comp.overlap_percentage,
  reasoning: comp.reasoning
}));

// Return structured data
return [
  {
    json: {
      brand_name: $input.first().json.brand_name,
      website_url: $input.first().json.website_url,
      niche: $input.first().json.niche,
      product_type: $input.first().json.product_type,
      user_email: $input.first().json.user_email,
      competitor_urls: competitorData.map(c => c.competitor_url),
      competitors: competitorData
    }
  }
];

// Get the Firecrawl job ID from the response
const jobId = $input.first().json.id;

return [{
  json: {
    job_id: jobId,
    url: $input.first().json.url
  }
}];

