// Function to trim LinkedIn job URLs
const trimLinkedInJobUrl = (url) => {
  const match = url.match(/^(https:\/\/www\.linkedin\.com\/jobs\/view\/\d+)/);
  return match ? match[1] : url;
};

// Main n8n Code node
return items.map(item => {
    // Access data
    const skills = item.json.output.skills || [];
    const tools = item.json.output.tools_expertise || [];

    // Get LinkedIn URL from Telegram Trigger
    const rawUrl = $('Telegram Trigger').first().json.message.link_preview_options.url;
    const trimmedUrl = rawUrl ? trimLinkedInJobUrl(rawUrl) : null;

    // Build HTML for skills
    const htmlOutput = skills
        .map(skill => `<p class="rela-block list-thing">${skill}</p>`)
        .join("\n");

    // Build HTML for tools
    const htmlTools = tools
        .map(tool => `<p class="rela-block list-thing">${tool}</p>`)
        .join("\n");

    // Return data
    return {
        json: {
            skill: htmlOutput,
            tool: htmlTools,
            trimmed_url: trimmedUrl
        }
    };
});

