const date = new Date(rawDate).toLocaleString('ar-SA', {
  year: 'numeric',
  month: 'long',
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

// Return simplified JSON for downstream nodes
return [
  {
    json: {
      from: `${email.from.value[0]?.name || 'ÿ∫Ÿäÿ± ŸÖÿπŸÑŸàŸÖ'} <${email.from.value[0]?.address || 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ'}>`,
