parameters.jsCode:
// üì® ÿßÿ≥ÿ™ŸÑÿßŸÖ ÿ£ŸàŸÑ ÿ±ÿ≥ÿßŸÑÿ© ŸÖŸÜ Outlook Trigger
const emailArray = $input.all();
if (!emailArray.length) throw new Error('No email data received');
const email = emailArray[0].json;

// üß© Decode HTML entities
function decodeHtmlEntities(text) {
  if (!text) return '';
  const entities = {
    '&nbsp;': ' ',
    '&amp;': '&',
    '&quot;': '"',
    ''': "'",
    '&lt;': '<',
    '&gt;': '>',
  };
  return text.replace(/&[a-z#0-9]+;/gi, m => entities[m] || m);
}

// üì® Extract and clean body
let body = '';
if (email.body?.content) {
  if (email.body.contentType === 'html') {
    body = decodeHtmlEntities(
      email.body.content
        .replace(/<style[\s\S]*?<\/style>/gi, '')
        .replace(/<script[\s\S]*?<\/script>/gi, '')
        .replace(/<[^>]+>/g, '\n')
        .replace(/\n\s*\n+/g, '\n')
        .replace(/\s{2,}/g, ' ')
        .replace(/\u200B/g, '')
        .trim()
    );
  } else {
    body = decodeHtmlEntities(email.body.content.trim());
  }
}
if (!body && email.bodyPreview) body = decodeHtmlEntities(email.bodyPreview);
if (!body) body = 'No message content found';

// üß† Classification
const classification = email.inferenceClassification?.toLowerCase() || 'other';
const categoryLine = `üìÇ Category: ${classification.charAt(0).toUpperCase() + classification.slice(1)}`;

// üóìÔ∏è Format date
const date = email.receivedDateTime
  ? new Date(email.receivedDateTime).toLocaleString('ar-SA', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    })
  : 'Unknown date';

// ‚úÖ Output simplified JSON
return [
  {
    json: {
      id: email.id,
      from: `${email.from?.emailAddress?.name || 'Unknown'} <${email.from?.emailAddress?.address || 'Unknown'}>`,
      subject: email.subject || 'No Subject',
      date,
      classification,
      body: `${categoryLine}\n\n${body}`,
      rawHtml: email.body?.contentType === 'html' ? email.body.content : ''
    }
  }
];

